class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        @lru_cache(None)
        def minimumArea(r, rr, c, cc) -> int:
            # min/max col/row
            minR, minC = math.inf, math.inf
            maxR, maxC = 0, 0
            for i in range(r, rr+1):
                for j in range(c, cc +1):
                    if grid[i][j] == 1:
                        minR = min(i, minR)
                        minC = min(j, minC)
                        maxC = max(j, maxC)
                        maxR = max(i, maxR)

            return (maxC-minC+1) * (maxR-minR+1)
        m = len(grid)
        n = len(grid[0])
        res = math.inf
        # L shapes
        for i in range(m-1):
            for j in range(n-1):
                rest = min(minimumArea(0, i, j+1, n-1) + minimumArea(i+1, m-1, 0, n-1), 
                           minimumArea(i+1, m-1, 0, j) + minimumArea(0, m-1, j+1, n-1))
                res = min(res, minimumArea(0, i, 0, j)+rest)
        # vertical splits      
        r = m-1
        for j in range(n-1):
            a = minimumArea(0, r, 0, j)
            for k in range(j+1, n-1):
                rest = minimumArea(0, r, j+1, k) + minimumArea(0, r, k+1, n-1)
                res = min(res, a + rest)

            for k in range(m-1):
                rest = minimumArea(0, k, j+1, n-1) + minimumArea(k+1, m-1, j+1, n-1)
                res = min(res, a+rest)

        c = n-1
        for j in range(m-1):
            a = minimumArea(0, j, 0, c)
            # row
            for k in range(j+1, m-1):
                rest = minimumArea(j+1, k, 0, c) + minimumArea(k+1, m-1, 0, c)
                res = min(res, a + rest)
            # col
            for k in range(n-1):
                rest = minimumArea(j+1, m-1, 0, k) + minimumArea(j+1, m-1, k+1, n-1)
                res = min(res, a+rest)

        return res
