class Solution {
public:
    int findAnswer(vector<vector<int>>& grid, int strti,int strtj,int edi,int edj){
        int mn=1001,mx=-1,mn1=1001,mx1=-1;
        for(int i=strti;i<=edi;i++){
            for(int j=strtj;j<=edj;j++){
                if(grid[i][j]==1){
                    mn=min(mn,i);
                    mx=max(mx,i);
                    mn1= min(mn1,j);
                    mx1=max(mx1,j);
                }
            }
        }
        if(mx==-1)return 0;
        return (mx-mn+1)*(mx1-mn1+1);
    }
    int divideIntoTwoXaxis(vector<vector<int>>& grid,int strti,int strtj,int edi,int edj){
        int ans=1001;
        for(int i=strti+1;i<=edi;i++){
                ans=min(ans,findAnswer(grid,strti,strtj,i-1,edj)+findAnswer(grid,i,strtj,edi,edj));
        }
        return ans;
    }

    int divideIntoTwoYaxis(vector<vector<int>>& grid,int strti,int strtj,int edi,int edj){
        int ans=1001;
        for(int i=strtj+1;i<=edj;i++){
                ans=min(ans,findAnswer(grid,strti,strtj,edi,i-1)+findAnswer(grid,strti,i,edi,edj));
        }
        return ans;
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        //all y-axis parallel
        int ans=1001;
        for(int i=1;i<n;i++){
            // for(int j=i+1;j<n;j++){
                ans=min(ans,findAnswer(grid,0,0,i-1,m-1)+divideIntoTwoXaxis(grid,i,0,n-1,m-1));
            // }
        }
        //all x-axis
        for(int i=1;i<m;i++){
            // for(int j=i+1;j<m;j++){
                ans=min(ans,findAnswer(grid,0,0,n-1,i-1)+divideIntoTwoYaxis(grid,0,i,n-1,m-1));
            // }
        }

        //one x-axis other part divide it into two y axis
        for(int i=1;i<n;i++){
            ans=min(ans,findAnswer(grid,0,0,i-1,m-1)+divideIntoTwoYaxis(grid,i,0,n-1,m-1));
            ans=min(ans,findAnswer(grid,i,0,n-1,m-1)+divideIntoTwoYaxis(grid,0,0,i-1,m-1));
        }

        //one y-axis other part divide it into two x axis
        for(int i=1;i<m;i++){
            ans=min(ans,findAnswer(grid,0,0,n-1,i-1)+divideIntoTwoXaxis(grid,0,i,n-1,m-1));
            ans=min(ans,findAnswer(grid,0,i,n-1,m-1)+divideIntoTwoXaxis(grid,0,0,n-1,i-1));
        }
        return ans;
    }
};
