class Solution {
    public int minimumSum(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int[][][][] dp = new int[m][n][m][n];
        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                for(int ii = i; ii < m; ii++){
                    for(int jj = j; jj < n; jj++){
                        dp[i][j][ii][jj] = f(grid, i, ii, j, jj);
                    }
                }
            }
        }
        // System.out.println(dp[0][0][m-1][n-1]);
        int ret = Integer.MAX_VALUE;
        for(int i = 0; i < n; i++){
            for(int j = i + 1; j < n-1; j++){
                ret = Math.min(ret, dp[0][0][m-1][i] + dp[0][i+1][m-1][j] + dp[0][j+1][m-1][n-1]);
            }
        }
        for(int i = 0; i < n-1; i++){
            for(int j = 0; j < m - 1; j++){
                ret = Math.min(ret, dp[0][0][m-1][i] + dp[0][i+1][j][n-1] + dp[j+1][i+1][m-1][n-1]);
            }
        }
        for(int i = n-1; i > 0; i--){
            for(int j = 0; j < m - 1; j++){
                ret = Math.min(ret, dp[0][i][m-1][n-1] + dp[0][0][j][i-1] + dp[j+1][0][m-1][i-1]);
            }
        }
        for(int i = 0; i < m; i++){
            for(int j = i+1; j < m-1; j++){
                ret = Math.min(ret, dp[0][0][i][n-1] + dp[i+1][0][j][n-1] + dp[j+1][0][m-1][n-1]);
            }
        }
        for(int i = 0; i < m-1; i++){
            for(int j = 0; j < n-1; j++){
                ret = Math.min(ret, dp[0][0][i][n-1] + dp[i+1][0][m-1][j] + dp[i+1][j+1][m-1][n-1]);
            }
        }
        for(int i = m-1; i > 0; i--){
            for(int j = 0; j < n-1; j++){
                ret = Math.min(ret, dp[i][0][m-1][n-1] + dp[0][0][i-1][j] + dp[0][j+1][i-1][n-1]);
            }
        }
        return ret;
    }
    public int f(int[][] grid, int si, int ei, int sj, int ej) {
        int cl = 1000000, cr = -1, rl = 1000000, rr = -1;
        for(int i = si; i <= ei; i++){
            for(int j = sj; j <= ej; j++){
                if(grid[i][j] == 1){
                    cl = Math.min(cl, j);
                    cr = Math.max(cr, j);
                    rl = Math.min(rl, i);
                    rr = Math.max(rr, i);
                }
            }
        }
        if(cl == 1000000) return 0;

        return (rr - rl + 1)*(cr - cl +1);
    }
}
