/**
 * @param {number[][]} grid
 * @return {number}
 */
var minimumSum = function(grid) {
    const m = grid.length, n = grid[0].length;
    let rmax = -1, rmin = m, cmax = -1, cmin = n;
    let min = m * n;
    const f = (t,b,l,r) => {
        let rmax = t - 1, rmin = b, cmax = l - 1, cmin = r;
        for(let i = t; i < b; i++) {
            for(let j = l; j < r; j ++) {
                if(grid[i][j]) {
                    rmax = Math.max(rmax,i);
                    cmax = Math.max(cmax,j);
                    rmin = Math.min(rmin,i);
                    cmin = Math.min(cmin,j);
                }
            }
        }
        if(rmax == t - 1) {
            return 0;
        }
        return (rmax - rmin + 1) * (cmax - cmin + 1);
    };
    for(let i = 0; i < m - 1; i++) {
        let st = f(0,i+1,0,n);
        if(st) {
            for(let j = 0; j < n - 1; j++) {
                let sl = f(i+1,m,0,j + 1);
                if(sl) {
                    let sr = f(i+1,m,j+1,n);
                    if(sr) {
                        min = Math.min(min,st+sl+sr);
                    }
                }
            }
            for(let j = i +1; j < m - 1;j++) {
                let st1 = f(i+1,j + 1,0,n);
                if(st1) {
                    let st2 = f(j+1,m,0,n);
                    if(st2) {
                        min = Math.min(min,st+st1+st2);
                    }
                }
            }
        }
    }
    for(let i = m - 1; i > 0; i--) {
        let st = f(i,m,0,n);
        if(st) {
            for(let j = 0; j < n - 1; j++) {
                let sl = f(0,i,0,j + 1);
                if(sl) {
                    let sr = f(0,i,j+1,n);
                    if(sr) {
                        min = Math.min(min,st+sl+sr);
                    }
                }
            }
        }
    }
    for(let j = 0; j < n - 1; j++) {
        let sl = f(0,m,0,j + 1);
        if(sl) {
            for(let i = 0; i < m - 1; i++) {
                let st = f(0,i+1,j+1,n);
                if(st) {
                    let sb = f(i+1,m,j+1,n);
                    if(sb) {
                        min = Math.min(min,st+sl+sb);
                    }
                }
            }
            for(let i = j +1; i < n - 1;i++) {
                let sl1 = f(0,m,j+1,i+1);
                if(sl1) {
                    let sl2 = f(0,m,i+1,n);
                    if(sl2) {
                        min = Math.min(min,sl+sl1+sl2);
                    }
                }
            }
        }
    }
    for(let j = n - 1; j > 0; j--) {
        let sl = f(0,m,j,n);
        if(sl) {
            for(let i = 0; i < m - 1; i++) {
                let st = f(0,i+1,0,j);
                if(st) {
                    let sb = f(i+1,m,0,j);
                    if(sb) {
                        min = Math.min(min,st+sl+sb);
                    }
                }
            }
        }
    }
    return min;
};
