class Solution {
public:
    int calc_s(int l,int r,int t,int b,vector<vector<int>>& grid)
    {
        int min_x=1e9,max_x=-1,min_y=1e9,max_y=-1;
        for(int i=t;i<=b;i++)
            for(int j=l;j<=r;j++)
                if(grid[i][j])
                {
                    min_x=min(min_x,j);
                    max_x=max(max_x,j);
                    min_y=min(min_y,i);
                    max_y=max(max_y,i);
                }
        if(min_x==1e9)
            return 0;
        return (max_x-min_x+1)*(max_y-min_y+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int l1,r1,t1,b1,l2,r2,t2,b2;
        int tot_s=1e9;
        int res=1e9;
        for(int i=1;i<n;i++)
        {
            for(int j=1;j<m;j++)
            {
                int t1=calc_s(0,m-1,0,i-1,grid),t2=calc_s(j,m-1,0,n-1,grid);
                int t3=calc_s(0,m-1,i,n-1,grid),t4=calc_s(0,j-1,0,n-1,grid);
                int s1=calc_s(0,j-1,0,i-1,grid),s2=calc_s(j,m-1,0,i-1,grid);
                int s3=calc_s(0,j-1,i,n-1,grid),s4=calc_s(j,m-1,i,n-1,grid);
                res=min(res,t1+s3+s4);
                res=min(res,t2+s1+s3);
                res=min(res,t3+s1+s2);
                res=min(res,t4+s2+s4);
            }
        }
        for(int i=1;i<n;i++)
            for(int j=i+1;j<n;j++)
            {
                res=min(res,calc_s(0,m-1,0,i-1,grid)+calc_s(0,m-1,i,j-1,grid)
                       +calc_s(0,m-1,j,n-1,grid));
            }
        for(int i=1;i<m;i++)
            for(int j=i+1;j<m;j++)
            {
                res=min(res,calc_s(0,i-1,0,n-1,grid)+calc_s(i,j-1,0,n-1,grid)
                       +calc_s(j,m-1,0,n-1,grid));
            }
        return res;
    }
};
