class Solution {
public:
    int solve(int topLeftX, int topLeftY, int bottomRightX, int bottomRightY, vector<vector<int>>& grid){
        int u = 30, d = 0, l = 30, r = 0;
        for(int i = topLeftY; i <= bottomRightY; i++){
            for(int j = topLeftX; j <= bottomRightX; j++){
                if(!grid[i][j]) continue;
                u = min(u, i);
                d = max(d, i);
                l = min(l, j);
                r = max(r, j);
            }
        }
        return max(1, (d - u + 1) * (r - l + 1));
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 1000;
        for(int i = 0; i < m - 2; i++){
            for(int j = i + 1; j < m - 1; j++){
                int temp = solve(0, 0, i, n-1, grid) + solve(i + 1, 0, j, n-1, grid) + solve(j + 1, 0, m-1, n-1, grid);
                ans = min(ans, temp);
            }
        }
        for(int i = 0; i < n - 2; i++){
            for(int j = i + 1; j < n - 1; j++){
                int temp = solve(0, 0, m-1, i, grid) + solve(0, i + 1, m-1, j, grid) + solve(0, j + 1, m-1, n-1, grid);
                ans = min(ans, temp);
            }
        }
        for(int i = 0; i < n - 1; i++){
            for(int j = 0; j < m - 1; j++){
                int temp = solve(0, 0, j, i, grid) + solve(0, i + 1, j, n-1, grid) + solve(j + 1, 0, m-1, n-1, grid);
                ans = min(ans, temp);
                temp = solve(0, 0, j, n-1, grid) + solve(j + 1, 0, m - 1, i, grid) + solve(j + 1, i + 1, m-1, n-1, grid);
                ans = min(ans, temp);
                temp = solve(0, 0, j, i, grid) + solve(j + 1, 0, m - 1, i, grid) + solve(0, i + 1, m-1, n-1, grid);
                ans = min(ans, temp);
                temp = solve(0, 0, m - 1, i, grid) + solve(0, i + 1, j, n - 1, grid) + solve(j + 1, i + 1, m-1, n-1, grid);
                ans = min(ans, temp);
            }
        }
        return ans;
    }
};
