class Solution {
public:
    long long go(int sx, int sy, int ex, int ey, vector<vector<int>>& grid)
    {
        // cout<<sx<<" "<<sy<<" "<<ex<<" "<<ey<<endl;
        int l=100, r=-100, u=100, d=-100;
        for(int i=sx; i<=ex; i++)
        {
            for(int j=sy; j<=ey; j++)
            {
                if(grid[i][j]==1)
                {
                    l=min(l, j);
                    r=max(r, j);
                    u=min(u, i);
                    d=max(d, i);
                }
            }
        }
        // cout<<l<<" "<<r<<" "<<u<<" "<<d<<endl;
        return (r-l+1)*1ll*(d-u+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size(), m=grid[0].size();
        long long ans=1e9;
        for(int i=0; i<n-1; i++)
        {
            for(int j=0; j<m; j++)
            {
                long long op1=go(0, 0, i, m-1, grid)+go(i+1, 0, n-1, j, grid)+go(i+1, j+1, n-1, m-1, grid);
                long long op2=go(i+1, 0, n-1, m-1, grid)+go(0, 0, i, j, grid)+go(0, j+1, i, m-1, grid);
                long long op3=go(0, 0, n-1, j, grid)+go(0, j+1, i, m-1, grid)+go(i+1, j+1, n-1, m-1, grid);
                long long op4=go(0, 0, i, j, grid)+go(i+1, 0, n-1, j, grid)+go(0, j+1, n-1, m-1, grid);
                ans=min({ans, op1, op2, op3, op4});
            }
        }

        for(int i=0; i<n-1; i++)
        {
            for(int j=i+1; j<n-1; j++)
            {
                // cout<<go(0, 0, i, m-1, grid)+go(i+1, 0, j, m-1, grid)+go(j+1, 0, n-1, m-1, grid)<<endl;
                ans=min(ans, go(0, 0, i, m-1, grid)+go(i+1, 0, j, m-1, grid)+go(j+1, 0, n-1, m-1, grid));
            }
        }

        for(int i=0; i<m-1; i++)
        {
            for(int j=i+1; j<m-1; j++)
            {
                ans=min(ans, go(0, 0, n-1, i, grid)+go(0, i+1, n-1, j, grid)+go(0, j+1, n-1, m-1, grid));
            }
        }
        return ans;
    }
};
