    int calrow(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();

        int fix = 1e8;
        int ans = fix;
        for(int i1 = 0; i1 < m; i1++) {
            for(int i2 = i1 + 1; i2 < m; i2++) {
                int i3 = i2 + 1;
                if(i3 < m) {
                    int sum = 0;

                    int x = res(a, 0, 0, n - 1, i1);
                    if(x == -1) x = fix;
                    sum += x;

                    x = res(a, 0, i1 + 1, n - 1, i2);
                    if(x == -1) x = fix;
                    sum += x;

                    x = res(a, 0, i2 + 1, n - 1, m - 1);
                    if(x == -1) x = fix;
                    sum += x;

                    if(sum < fix) {
                        ans = min(ans, sum);
                    }
                }
            }
        }

        return ans;
    }

