                    }
                }
            }
        }

        return ans;
    }

    int res(vector<vector<int>>& a, int i1, int j1, int i2, int j2) {
        int cmn = 10000000, cmx = -10000000;
        int rmn = 10000000, rmx = -10000000;
        for(int i = i1; i <= i2; i++) {
            for(int j = j1; j <= j2; j++) {
                if(a[i][j] == 1) {
                    cmn = min(cmn, j);
                    cmx = max(cmx, j);

                    rmn = min(rmn, i);
                    rmx = max(rmx, i);
                }
            }
        }

        int fix = -1;
        if(cmn == 10000000 || rmx == 10000000) return fix;
        if(cmx == -10000000 || rmx == -10000000) return fix;

        int ans = (rmx - rmn + 1) * (cmx - cmn + 1);
        return ans;
    }
