class Solution {
public:
    int msx(vector<vector<int>>& grid, int x0, int x1, int y0, int y1){
        int min_x = 1001, max_x = -1, min_y = 1001, max_y = -1;
        for(int i=x0;i<=x1;i++){
            for(int j=y0;j<=y1;j++){
                if(grid[i][j] == 1){
                    min_x = min(min_x,i);
                    min_y = min(min_y,j);
                    max_x = max(max_x,i);
                    max_y = max(max_y,j);
                }
            }
        }
        if(max_x == -1 || max_y == -1){return 0;}
        return (max_x-min_x+1)*(max_y-min_y+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int ans = INT_MAX;
        int row=grid.size(), col=grid[0].size();
        int hz[row+1][row+1], vr[col+1][col+1];
        for(int i0=0;i0<=row;i0++){
            for(int i1=0;i1<=row;i1++){
				hz[i0][i1] = 0;
			}
		}
        for(int j0=0;j0<=col;j0++){
            for(int j1=0;j1<=col;j1++){
				vr[j0][j1] = 0;
			}
		}

        for(int i0=0;i0<row;i0++){
            for(int i1=i0;i1<row;i1++){
                hz[i0][i1] = msx(grid,i0,i1,0,col-1);
            }
        }
        for(int i0=1;i0<row;i0++){
            for(int i1=i0;i1+1<row;i1++){
                int c0 = hz[0][i0-1], c1 = hz[i0][i1], c2 = hz[i1+1][row-1];
                if(c0 != 0 && c2 != 0 && c2 != 0){ans = min(ans,c0+c1+c2);}
            }
        }

        for(int j0=0;j0<col;j0++){
            for(int j1=j0;j1<col;j1++){
                vr[j0][j1] = msx(grid,0,row-1,j0,j1);
            }
        }
        for(int j0=1;j0<col;j0++){
            for(int j1=j0;j1+1<col;j1++){
                int c0 = vr[0][j0-1], c1 = vr[j0][j1], c2 = vr[j1+1][col-1];
                if(c0 != 0 && c2 != 0 && c2 != 0){ans = min(ans,c0+c1+c2);}
            }
        }

        int q1[row][col], q2[row][col], q3[row][col], q4[row][col];
        for(int i=0;i<row;i++){
            for(int j=0;j<col;j++){
                q1[i][j] = msx(grid,0,i,j,col-1);
                q2[i][j] = msx(grid,0,i,0,j);
                q3[i][j] = msx(grid,i,row-1,0,j);
                q4[i][j] = msx(grid,i,row-1,j,col-1);
            }
        }
        for(int i=1;i<row;i++){
            for(int j=1;j<col;j++){
				int c0, c1, c2;
				// printf("%d\n",ans);
                c0 = q2[i-1][j-1]; c1 = q3[i][j-1]; c2 = vr[j][col-1];
                if(c0 != 0 && c1 != 0 && c2 != 0){ans = min(ans,c0+c1+c2);}
				// printf("%d %d %d %d\n",ans,c0,c1,c2);
                c0 = q1[i-1][j]; c1 = q4[i][j]; c2 = vr[0][j-1];
                if(c0 != 0 && c1 != 0 && c2 != 0){ans = min(ans,c0+c1+c2);}
				// printf("%d %d %d %d\n",ans,c0,c1,c2);
                c0 = q2[i-1][j-1]; c1 = q1[i-1][j]; c2 = hz[i][row-1];
                if(c0 != 0 && c1 != 0 && c2 != 0){ans = min(ans,c0+c1+c2);}
				// printf("%d %d %d %d\n",ans,c0,c1,c2);
                c0 = q3[i][j-1]; c1 = q4[i][j]; c2 = hz[0][i-1];
                if(c0 != 0 && c1 != 0 && c2 != 0){ans = min(ans,c0+c1+c2);}
				// printf("%d %d %d %d\n",ans,c0,c1,c2);
            }
        }
        return ans;
    }
};
