class Solution {
public:
    int calc(vector<vector<int>>& grid, int l1, int r1, int l2, int r2) {
        int st1 = -1, ed1 = -1;
        int st2 = -1, ed2 = -1;
        for(int i = l1; i <= r1; i ++) {
            for(int j = l2; j <= r2; j++) {
                if(grid[i][j] == 1 && st1 == -1) st1 = i;
                if(grid[i][j] == 1) ed1 = i; 
            }
        }
        for(int i = l2; i <= r2; i ++) {
            for(int j = l1; j <= r1; j++) {
                if(grid[j][i] == 1 && st2 == -1) st2 = i;
                if(grid[j][i] == 1) ed2 = i; 
            }
        }
        return (ed2 - st2 + 1) * (ed1 - st1 + 1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int st1 = -1, ed1 = -1;
        int st2 = -1, ed2 = -1;
        for(int i = 0; i < m; i ++) {
            for(int j = 0; j < n; j++) {
                if(grid[i][j] == 1 && st1 == -1) st1 = i;
                if(grid[i][j] == 1) ed1 = i; 
            }
        }
        for(int i = 0; i < n; i ++) {
            for(int j = 0; j < m; j++) {
                if(grid[j][i] == 1 && st2 == -1) st2 = i;
                if(grid[j][i] == 1) ed2 = i; 
            }
        }
        int res = INT_MAX;
        if(st1 == ed1) {
            for(int i = st2; i <= ed2 - 2; i ++) {
                for(int j = i + 1; j < ed2; j ++) {
                    res = min(res, calc(grid, st1, ed1, st2, i) + calc(grid, st1, ed1, i + 1, j) + calc(grid, st1, ed1, j + 1, ed2));
                }
            }
            return res;
        }
        if(st2 == ed2) {
            for(int i = st1; i <= ed1 - 2; i ++) {
                for(int j = i + 1; j < ed1; j ++) {
                    res = min(res, calc(grid, st1, i, st2, ed2) + calc(grid, i + 1, j, st2, ed2) + calc(grid, j + 1, ed1, st2, ed2));
                }
            }
            return res;
        }
        // 分割 1
        // [st1, i] 
        // [i+1, ed1] 继续分割
        // cout << st2 << ' ' << ed2 << endl;
        if(ed1 > st1 + 1) { // 可以分割三行
            for(int i = st1; i <= ed1 - 2; i ++) {
                for(int j = i + 1; j < ed1; j ++) {
                    res = min(res, calc(grid, st1, i, st2, ed2) + calc(grid, i + 1, j, st2, ed2) + calc(grid, j + 1, ed1, st2, ed2));
                }
            }
        }
        if(ed2 > st2 + 1) {
            for(int i = st2; i <= ed2 - 2; i ++) {
                for(int j = i + 1; j < ed2; j ++) {
                    res = min(res, calc(grid, st1, ed1, st2, i) + calc(grid, st1, ed1, i + 1, j) + calc(grid, st1, ed1, j + 1, ed2));
                }
            }
        }
        for(int i = st1; i <= ed1 - 1; i ++) {
            for(int j = st2; j <= ed2 - 1; j ++) {
                res = min(res, calc(grid, st1, i, st2, ed2) + calc(grid, i + 1, ed1, st2, j) + calc(grid, i + 1, ed1, j + 1, ed2));
            }
        }
        for(int i = st2; i <= ed2 - 1; i ++) {
            for(int j = st1; j <= ed1 - 1; j ++) {
                res = min(res, calc(grid, st1, ed1, st2, i) + calc(grid, st1, j, i + 1, ed2) + calc(grid, j + 1, ed1, i + 1, ed2));
            }
        }
        for(int i = st1 + 1; i <= ed1; i ++) {
            for(int j = st2; j <= ed2 - 1; j ++) {
                res = min(res, calc(grid, i, ed1, st2, ed2) + calc(grid, st1, i - 1, st2, j) + calc(grid, st1, i - 1, j + 1, ed2));
            }
        }
        for(int i = st2 + 1; i <= ed2; i ++) {
            for(int j = st1; j <= ed1 - 1; j ++) {
                res = min(res, calc(grid, st1, ed1, i, ed2) + calc(grid, st1, j, st2, i-1) + calc(grid, j + 1, ed1, st2, i-1));
            }
        }
        return res;
    }
};
