class Solution {
public:
    int findMin(vector<vector<int>> &mat, int rStart, int rEnd, int cStart, int cEnd) {
        int minRow = rEnd, maxRow = rStart, minCol = cEnd, maxCol = cStart;
        for(int i = rStart; i <= rEnd; i++) 
            for(int j = cStart; j <= cEnd; j++)
            {
                if(mat[i][j] == 1){
                    minRow = min(minRow, i);
                    maxRow = max(maxRow, i);
                    minCol = min(minCol, j);
                    maxCol = max(maxCol, j);
                }
            }


        return max(0, (maxCol-minCol+1) * (maxRow-minRow+1));
    }

    int minimumSum(vector<vector<int>>& mat) {
        int n = mat.size(), m = mat[0].size(), minRow = n-1, maxRow = 0, minCol = m-1, maxCol = 0, ans = n*m;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                int leftTop, rightTop, bottomLeft, bottomRight, top, left, bottom, right;
                leftTop = findMin(mat, 0, i-1, 0, j-1);
                rightTop = findMin(mat, 0, i-1, j, m-1);
                bottomLeft = findMin(mat, i, n-1, 0, j-1);
                bottomRight = findMin(mat, i, n-1, j, m-1);
                top = findMin(mat, 0, i-1, 0, m-1);
                left = findMin(mat, 0, n-1, 0, j-1);
                bottom = findMin(mat, i, n-1, 0, m-1);
                right = findMin(mat, 0, n-1, j, m-1);

                ans = min({ans, 
                           leftTop + rightTop + bottom, 
                           bottomLeft + bottomRight + top, 
                           rightTop + bottomRight + left,
                           leftTop + bottomLeft + right});

            }
        }

        for(int i = 0; i < n; i++) {
            for(int j = i+1; j < n; j++) {
                int top, middle, bottom;
                top = findMin(mat, 0, i-1, 0, m-1);
                middle = findMin(mat, i, j-1, 0, m-1);
                bottom = findMin(mat, j, n-1, 0, m-1);
                ans = min({ans, top + middle + bottom});
            }
        }

        for(int i = 0; i < m; i++) {
            for(int j = i+1; j < m; j++) {
                int left, middle, right;
                left = findMin(mat, 0, n-1, 0, i-1);
                middle = findMin(mat, 0, n-1, i, j-1);
                right = findMin(mat, 0, n-1, j, m-1);

                ans = min({ans, left+middle+right});
            }
        }

        return ans;
    }
};
