class Solution {
public:
    int minimumArea(vector<vector<int>>& grid, int li, int ri, int lj, int rj) {
        int l = rj, r = -1, u = ri, d = -1;
        for(int i = li; i < ri; ++i) {
            for(int j = lj; j < rj; ++j) {
                if(grid[i][j]) {
                    l = min(l, j);
                    r = max(r, j);
                    u = min(u, i);
                    d = max(d, i);
                }
            }
        }
        if(r == -1) return 0;
        return (r - l + 1) * (d - u + 1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = n * m;
        for(int i = 0; i < n; ++i) {
            for(int j = 0; j < m; ++j) {
                cout << grid[i][j] << " \n"[j == m - 1];
            }
        }
        for(int i1 = 1; i1 <= n; ++i1) {
            for(int i2 = i1 + 1; i2 <= n; ++i2) {
                ans = min(ans, minimumArea(grid, 0, i1, 0, m) + minimumArea(grid, i1, i2, 0, m) + minimumArea(grid, i2, n, 0, m));
            }
            for(int j1 = 1; j1 <= m; ++j1) {
                ans = min(ans, minimumArea(grid, 0, i1, 0, m) + minimumArea(grid, i1, n, 0, j1) + minimumArea(grid, i1, n, j1, m));
                ans = min(ans, minimumArea(grid, 0, i1, 0, j1) + minimumArea(grid, 0, i1, j1, m) + minimumArea(grid, i1, n, 0, m));
            }
        }
        for(int j1 = 1; j1 <= m; ++j1) {
            for(int j2 = j1 + 1; j2 <= m; ++j2) {
                ans = min(ans, minimumArea(grid, 0, n, 0, j1) + minimumArea(grid, 0, n, j1, j2) + minimumArea(grid, 0, n, j2, m));
            }
            for(int i1 = 1; i1 <= n; ++i1) {
                ans = min(ans, minimumArea(grid, 0, n, 0, j1) + minimumArea(grid, 0, i1, j1, m) + minimumArea(grid, i1, n, j1, m));
                ans = min(ans, minimumArea(grid, 0, i1, 0, j1) + minimumArea(grid, i1, n, 0, j1) + minimumArea(grid, 0, n, j1, m));
            }
        }
        return ans;
    }
};
