class Solution {
public:
    int check(vector<vector<int>>& grid,int a,int b,int c,int d){
        int l1=INT_MAX,l2 = 0,h1 = INT_MAX,h2 = 0;
        int m = grid.size(),n = grid[0].size();
        for(int i = a;i <= b;i++)
        for(int j = c;j <= d;j++){
            if(grid[i][j] == 0)continue;
            l1 = min(l1,i);
            l2 = max(l2,i);
            h1 = min(h1,j);
            h2 = max(h2,j);
        }
        if(l1 == INT_MAX)return 0;
        return (l2 - l1 + 1) * (h2-h1 + 1);
    }
    int solve(vector<vector<int>>& grid,int a,int b,int c,int d,int cnt){
        if(cnt == 1)return check(grid,a,b,c,d);
        int s = INT_MAX;
        for(int i = a;i < b;i++){
            s = min(s,check(grid,a,i,c,d) + check(grid,i + 1,b,c,d));
        }
        for(int j = c;j < d;j++){
            s = min(s,check(grid,a,b,c,j) + check(grid,a,b,j + 1,d));
        }
        return s;
    }
    int minimumSum(vector<vector<int>>& grid) {
        int l1=INT_MAX,l2 = 0,h1 = INT_MAX,h2 = 0;
        int m = grid.size(),n = grid[0].size();
        int a = 0,b = m-1,c = 0,d = n-1;
        int s = INT_MAX;
        for(int i = a;i < b;i++){
            s = min(s,solve(grid,a,i,c,d,2) + solve(grid,i + 1,b,c,d,1));
            s = min(s,solve(grid,a,i,c,d,1) + solve(grid,i + 1,b,c,d,2));
        }
        for(int i = c;i < d;i++){
            s = min(s,solve(grid,a,b,c,i,2) + solve(grid,a,b,i + 1,d,1));
            s = min(s,solve(grid,a,b,c,i,1) + solve(grid,a,b,i + 1,d,2));
        }
        return s;
    }
};
