class Solution {
public:
    int findmin(vector<vector<int>>& grid,int x1, int x2,int y1, int y2) {
        int x11 = -1;
        int x21 = -1;
        int y11 = -1;
        int y21 = -1;
        for (int i = x1;i<=x2;i++){
            for (int j = y1;j<=y2;j++){
                if (grid[i][j]){
                    x11 = i;
                    break;
                }
            }
            if (x11 != -1){
                break;
            }
        }
        for (int i = x2;i>=x1;i--){
            for (int j = y1;j<=y2;j++){
                if (grid[i][j]){
                    x21 = i;
                    break;
                }
            }
            if (x21 != -1){
                break;
            }
        }
        for (int i = y1;i<=y2;i++){
            for (int j = x1;j<=x2;j++){
                if (grid[j][i]){
                    y11 = i;
                    break;
                }
            }
            if (y11 != -1){
                break;
            }
        }
        for (int i = y2;i>=y1;i--){
            for (int j = x1;j<=x2;j++){
                if (grid[j][i]){
                    y21 = i;
                    break;
                }
            }
            if (y21 != -1){
                break;
            }
        }
        if (x11 == -1){
            return 0;
        }
        int ans = (abs(y21-y11) + 1)*(abs(x21-x11)+1);
        return ans;
    }

    int helper(vector<vector<int>>& grid,int x1,int x2,int y1,int y2,int k){
        if (k==0){
            return findmin(grid,x1,x2,y1,y2);
        }
        // iterate through all rows
        int ans = 10000;
        for (int i = x1;i<x2;i++){
            ans = min(ans,findmin(grid,x1,i,y1,y2) + helper(grid,i+1,x2,y1,y2,k-1));
        }
        for (int i = x2;i>x1;i--){
            ans = min(ans,findmin(grid,i,x2,y1,y2) + helper(grid,x1,i-1,y1,y2,k-1));
        }
        for (int i = y1;i<y2;i++){
            ans = min(ans,findmin(grid,x1,x2,y1,i) + helper(grid,x1,x2,i+1,y2,k-1));
        }
        for (int i = y2;i>y1;i--){
            ans = min(ans,findmin(grid,x1,x2,i,y2) + helper(grid,x1,x2,y1,i-1,k-1));
        }
        return ans;
    }
