            }
            if (x1 != -1){
                break;
            }
        }
        for (int i = 0;i<n;i++){
            for (int j = 0;j<m;j++){
                if (grid[n-1-i][j]){
                    x2 = n-1-i;
                    break;
                }
            }
            if (x2 != -1){
                break;
            }
        }
        for (int i = 0;i<m;i++){
            for (int j = 0;j<n;j++){
                if (grid[j][i]){
                    y1 = i;
                    break;
                }
            }
            if (y1 != -1){
                break;
            }
        }
        for (int i = 0;i<m;i++){
            for (int j = 0;j<n;j++){
                if (grid[j][m-1-i]){
                    y2 = m-1-i;
                    break;
                }
            }
            if (y2 != -1){
                break;
            }
        }
        cout<<x1<<y1<<x2<<y2;
        return helper(grid,x1,x2,y1,y2,2);
    }
};
