class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:

        def check(ri,ra,ci,ca):
            assert(ri >= 0)
            assert(ra < m)
            assert(ci >= 0)
            assert(ca < n)
            assert( ri <= ra )
            assert( ci <= ca )

            l,t = ca,ra
            r,b = ci,ri
            flag = 0

            for i in range(ri,ra+1):
                for j in range(ci,ca+1):
                    if grid[i][j]:
                        flag = 1
                        l = min(l, j)
                        t = min(t, i)
                        r = max(r, j)
                        b = max(b, i)

            ret = (r-l+1) * (b-t+1)
            return ret if flag else 0 


        m = len(grid)
        n = len(grid[0])
        ans = inf 

        for rc in range(1,m):
            for lc in range(1,n):
                ans = min(ans,
                    check(0, rc-1, 0, n-1) + \
                    check(rc, m-1, 0, lc-1) + \
                    check(rc, m-1, lc, n-1)
                )

                ans = min(ans,
                    check(rc, m-1, 0, n-1) + \
                    check(0, rc-1, 0, lc-1) + \
                    check(0, rc-1, lc, n-1)
                )

        for lc in range(1,n):
            for rc in range(1,m):
                ans = min(ans,
                    check(0, m-1,  0, lc-1) + \
                    check(0,rc-1,  lc, n-1 ) + \
                    check(rc, m-1, lc, n-1)
                )
                ans = min(ans,
                    check(0, m-1,  lc, n-1) + \
                    check(0,rc-1,  0, lc-1 ) + \
                    check(rc, m-1, 0, lc-1)
                )

        for r1 in range(1,m-1):
            for r2 in range(r1+1,m):
                ans = min(ans, check(0,r1-1, 0,n-1) + check(r1,r2-1,0,n-1) + check(r2,m-1,0,n-1)) 

        for c1 in range(1,n-1):
            for c2 in range(c1+1,n):
                ans = min(ans, check(0,m-1, 0,c1-1) + check(0,m-1,c1,c2-1) + check(0,m-1,c2,n-1)) 

        return ans
