class Solution {
public:
    vector<vector<int>> grid;
    int m, n;
    int minimumSum(vector<vector<int>>& grid) {
        this->grid = grid;
        m = grid.size();
        n = grid[0].size();
        int ans = m*n;
        for(int type = 1; type <= 6; type++) {
            ans = min(ans, get(type));
        }
        return ans;
    }

    int get(int tp) {
        int ans = 1e9;
        if(tp == 1) {
            for(int i1 = 0; i1 < m - 2; i1++) {
                for(int i2 = i1 + 1; i2 < m - 1; i2++) {
                    ans = min(ans, get(0, i1, 0, n - 1) + get(i1 + 1, i2, 0, n - 1) + get(i2 + 1, m - 1, 0, n - 1));
                }
            }
        }else if(tp == 2) {
            for(int j1 = 0; j1 < n - 2; j1++) {
                for(int j2 = j1 + 1; j2 < n - 1; j2++) {
                    ans = min(ans, get(0, m - 1, 0, j1) + get(0, m - 1, j1 + 1, j2) + get(0, m - 1, j2 + 1, n - 1));
                }
            }
        }else if(tp == 3) {
            for(int i = 0; i < m - 1; i++) {
                for(int j = 0; j < n - 1; j++) {
                    ans = min(ans, get(0, i, 0, j) + get(0, i, j + 1, n - 1) + get(i + 1, m - 1, 0, n - 1));
                }
            }
        }else if(tp == 4) {
            for(int i = 0; i < m - 1; i++) {
                for(int j = 0; j < n - 1; j++) {
                    ans = min(ans, get(i + 1, m - 1, 0, j) + get(i + 1, m - 1, j + 1, n - 1) + get(0, i, 0, n - 1));
                }
            }
        }else if(tp == 5) {
            for(int i = 0; i < m - 1; i++) {
                for(int j = 0; j < n - 1; j++) {
                    ans = min(ans, get(0, i, 0, j) + get(i + 1, m - 1, 0, j) + get(0, m - 1, j + 1, n - 1));
                }
            }
        }else {
            for(int i = 0; i < m - 1; i++) {
                for(int j = 0; j < n - 1; j++) {
                    ans = min(ans, get(0, m - 1, 0, j) + get(i + 1, m - 1, j + 1, n - 1) + get(0, i, j + 1, n - 1));
                }
            }
        }
        return ans;
    }

    int get(int t, int d, int l, int r) {
        int xmin = 1e9, xmax = -1, ymin = 1e9, ymax = -1, zero_cnt = 0;
        for(int i = t; i <= d; i++) {
            for(int j = l; j <= r; j++) {
                if(grid[i][j] == 1) {
                    zero_cnt++;
                    xmin = min(xmin, i);
                    xmax = max(xmax, i);
                    ymin = min(ymin, j);
                    ymax = max(ymax, j);
                }
            }
        }
        if(zero_cnt == 0) return 1000000;
        return (ymax - ymin + 1)*(xmax - xmin + 1);
    }
};
