class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        def find(top, bottom, left, right):
            t, b = inf, -inf
            l, r = inf, -inf
            c = 0
            for i in range(top, bottom):
                for j in range(left, right):
                    if grid[i][j] == 1:
                        c += 1
                        t = min(t, i)
                        b = max(b, i)
                        l = min(l, j)
                        r = max(r, j)
            if c == 0:
                return 0
            return (r - l + 1) * (b - t + 1)


        n, m = len(grid), len(grid[0])
        ans = n * m
        for i in range(1, n):
            top_center = find(0, i, 0, m)
            for j in range(1, m):
                bottom_left = find(i, n, 0, j)
                bottom_right = find(i, n, j, m)
                ans = min(ans, top_center + bottom_left + bottom_right)
            bottom_center = find(i, n, 0, m)
            for j in range(1, m):
                top_left = find(0, i, 0, j)
                top_right = find(0, i, j, m)
                ans = min(ans, bottom_center + top_left + top_right)
            for j in range(i + 1, n):
                mid = find(i, j, 0, m)
                bottom = find(j, n, 0, m)
                ans = min(ans, top_center + mid + bottom)

        for j in range(1, m):
            left_middle = find(0, n, 0, j)
            for i in range(1, n):
                right_top = find(0, i, j, m)
                right_bottom = find(i, n, j, m)
                ans = min(ans, left_middle + right_top + right_bottom)
            right_middle = find(0, n, j, m)
            for i in range(1, n):
                left_top = find(0, i, 0, j)
                left_bottom = find(i, n, 0, j)
                ans = min(ans, right_middle + left_top + left_bottom)
            for i in range(j+1, m):
                center = find(0, n, j, i)
                right = find(0, n, i, m)
                ans = min(ans, left_middle + center + right)

        return ans
