class Solution{
public:
  const int INF=1e6+5;
  int minimumSum(vector<vector<int>> &grid){
    int n=(int)grid.size();
    int m=(int)grid[0].size();
    auto call=[&](int r1,int c1,int r2,int c2){
      int minr,minc,maxr,maxc;
      minr=minc=max(n,m);
      maxr=maxc=-1;
      for(int i=r1;i<=r2;i++){
        for(int j=c1;j<=c2;j++){
          if(grid[i][j]==1){
            minr=min(minr,i);
            minc=min(minc,j);
            maxr=max(maxr,i);
            maxc=max(maxc,j);
          }
        }
      }
      int ans;
      if(maxr>=minr){
        ans=(maxr-minr+1)*(maxc-minc+1);
      }else{
        ans=INF;
      }
      // cout<<"  ->call("<<r1<<","<<c1<<","<<r2<<","<<c2<<")="<<ans<<"\n";
      return ans;
    };
    int ans=n*m;
    for(int x=0;x<n-1;x++){
      for(int y=0;y<m-1;y++){
        ans=min({ans,
                 call(0,0,x,y)+call(x+1,0,n-1,m-1)+call(0,y+1,x,m-1),
                 call(0,0,x,y)+call(x+1,0,n-1,y)+call(0,y+1,n-1,m-1)
                 });
      }
    }
    for(int y=0;y<m-1;y++){
      for(int x=0;x<n-1;x++){
        ans=min(ans,call(0,0,n-1,y)+call(0,y+1,x,m-1)+call(x+1,y+1,n-1,m-1));
      }
    }
    for(int x=0;x<n-1;x++){
      for(int y=0;y<m-1;y++){
        ans=min(ans,call(0,0,x,m-1)+call(x+1,0,n-1,y)+call(x+1,y+1,n-1,m-1));
      }
    }
    for(int y=0;y<m-2;y++){
      for(int y2=y+1;y2<m-1;y2++){
        ans=min(ans,call(0,0,n-1,y)+call(0,y+1,n-1,y2)+call(0,y2+1,n-1,m-1));
      }
    }
    for(int x=0;x<n-2;x++){
      for(int x2=x+1;x2<n-1;x2++){
        ans=min(ans,call(0,0,x,m-1)+call(x+1,0,x2,m-1)+call(x2+1,0,n-1,m-1));
      }
    }
    return ans;
  }
};
