class Solution {
public:
    int helper(vector<vector<int>>& grid, int start_col, int end_col, int start_row, int end_row) {
        bool hit = false;
        int left = 31, right = -1, up = 31, down = -1;
        for (int i = start_row; i <= end_row; i++) {
            for (int j = start_col; j <= end_col; j++) {
                if (grid[i][j] == 1) {
                    hit = true;
                    left = min(left, j);
                    right = max(right, j);
                    up = min(up, i);
                    down = max(down, i);
                }
            }
        }
        if (!hit) return 1001;
        return (right-left+1) * (down-up+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int res = 1000;
        int m = grid.size(), n = grid[0].size();
        for (int i = 0; i < n-1; i++) {
            int tmp = helper(grid, 0, i, 0, m-1);
            for (int j = 0; j < m-1; j++) {
                int tmp2 = helper(grid, i+1, n-1, 0, j) + helper(grid, i+1, n-1, j+1, m-1);
                res = min(res, tmp + tmp2);
            }
        }
        //cout << "res1:" << res << endl;
        for (int i = n-1; i > 0; i--) {
            int tmp = helper(grid, i, n-1, 0, m-1);
            for (int j = 0; j < m-1; j++) {
                int tmp2 = helper(grid, 0, i-1, 0, j) + helper(grid, 0, i-1, j+1, m-1);
                res = min(res, tmp + tmp2);
            }
        }
        //cout << "res2:" << res << endl;
        for (int i = m-1; i > 0; i--) {
            int tmp = helper(grid, 0, n-1, i, m-1);
            for (int j = 0; j < n-1; j++) {
                int tmp2 = helper(grid, 0, j, 0, i-1) + helper(grid, j+1, n-1, 0, i-1);
                res = min(res, tmp + tmp2);
            }
        }
        //cout << "res3:" << res << endl;
        for (int i = 0; i < m-1; i++) {
            int tmp = helper(grid, 0, n-1, 0, i);
            for (int j = 0; j < n-1; j++) {
                int tmp2 = helper(grid, 0, j, i+1, m-1) + helper(grid, j+1, n-1, i+1, m-1);
                res = min(res, tmp + tmp2);
            }
        }
        //cout << "res4:" << res << endl;
        for (int i = 0; i < n-2; i++) {
            for (int j = i+1; j < n-1; j++) {
                int tmp = helper(grid, 0, i, 0, m-1) + helper(grid, i+1, j, 0, m-1) + helper(grid, j+1, n-1, 0, m-1);
                res = min(res, tmp);
            }
        }
        //cout << "res5:" << res << endl;
        for (int i = 0; i < m-2; i++) {
            for (int j = i+1; j < m-1; j++) {
                int tmp = helper(grid, 0, n-1, 0, i) + helper(grid, 0, n-1, i+1, j) + helper(grid, 0, n-1, j+1, m-1);
                res = min(res, tmp);
            }
        }
        //cout << "res6:" << res << endl;


        return res;
    }
};
