class Solution {
private:
    int area(vector<vector<int>>& grid,int upR, int downR, int leftC, int rightC){
        int minR = downR;
        int minC = rightC;
        int maxR = upR;
        int maxC = leftC;
        for(int i = upR;i < downR;i++){
            for(int j = leftC;j < rightC;j++){
                if(grid[i][j] == 1){
                    minR = min(minR,i);
                    minC = min(minC,j);
                    maxR = max(maxR,i);
                    maxC = max(maxC,j);
                }
            }
        }
        // printf("[(%d,%d) -> (%d,%d) : %d]\n",upR,downR,leftC,rightC,(maxR-minR+1)*(maxC-minC+1));
        return (maxR-minR+1)*(maxC-minC+1);
    }
public:
    int minimumSum(vector<vector<int>>& grid) {

        int minArea = INT_MAX;
        int RIGHT = grid[0].size();
        int DOWN = grid.size();
        // first divisionLine( --- )
        for(int i = 1; i < DOWN;i++){
            // divide rectengle (---)
            for(int j = 1; j < i;j++){
                minArea = min(minArea,area(grid,0,j,0,RIGHT)+area(grid,j,i,0,RIGHT)+area(grid,i,DOWN,0,RIGHT));
            }
            // divide rectengle( | );
            for(int j = 1; j < RIGHT;j++){
                minArea = min(minArea,area(grid,0,i,0,j)+area(grid,0,i,j,RIGHT)+area(grid,i,DOWN,0,RIGHT));
                minArea = min(minArea,area(grid,0,i,0,RIGHT)+area(grid,i,DOWN,0,j)+area(grid,i,DOWN,j,RIGHT));
            }
        }
        // first divisionLine( | )
        for(int i = 1; i < RIGHT;i++){
            // divide rectengle ( | )
            for(int j = 1; j < i;j++){
                minArea = min(minArea,area(grid,0,DOWN,0,j)+area(grid,0,DOWN,j,i)+area(grid,0,DOWN,i,RIGHT));
            }
            // divide rectengle( --- );
            for(int j = 1; j < DOWN;j++){
                minArea = min(minArea,area(grid,0,j,0,i)+area(grid,j,DOWN,0,i)+area(grid,0,DOWN,i,RIGHT));
                minArea = min(minArea,area(grid,0,DOWN,0,i)+area(grid,0,j,i,RIGHT)+area(grid,j,DOWN,i,RIGHT));
            }
        }
        // printf("===============\n");
        return minArea;
    }
};
