class Solution {
public:

    int _solve(int x1,int x2,int y1,int y2,vector<vector<int>>& grid){
        int n=grid.size(),m=grid[0].size();

        int i1=n,j1=m,i2=-1,j2=-1;

        for(int i=x1;i<=x2;i++){
            for(int j=y1;j<=y2;j++){
                if(grid[i][j]==1){
                    i1 = min(i1,i);
                    j1 = min(j1,j);
                    i2 = max(i2,i);
                    j2 = max(j2,j);
                }
            }
        }

        if(i1==n || j1==m || i2==-1 || j2==-1){
            return 0;
        }

        int ans = (i2-i1+1)*(j2-j1+1);
        return ans;
    }


    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();

        int ans = n*m;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){

                //x1+x2

                int curr1 = _solve(0,i,0,m-1,grid);
                int curr2 = _solve(i+1,n-1,j+1,m-1,grid);
                int curr3 = _solve(i+1,n-1,0,j,grid);

                ans = min(ans,curr1+curr2+curr3);

                //x2+x3

                curr1 = _solve(0,n-1,j+1,m-1,grid);
                curr2 = _solve(i+1,n-1,0,j,grid);
                curr3 = _solve(0,i,0,j,grid);

                ans = min(ans,curr1+curr2+curr3);

                //x3+x4

                curr1 = _solve(i+1,n-1,0,m-1,grid);
                curr2 = _solve(0,i,0,j,grid);
                curr3 = _solve(0,i,j+1,m-1,grid);

                ans = min(ans,curr1+curr2+curr3);

                //x4+x1

                curr1 = _solve(0,n-1,0,j,grid);
                curr2 = _solve(0,i,j+1,m-1,grid);
                curr3 = _solve(i+1,n-1,j+1,m-1,grid);

                ans = min(ans,curr1+curr2+curr3);


            }
        }

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){

                int curr1 = _solve(0,i,0,m-1,grid);
                int curr2 = _solve(i+1,j,0,m-1,grid);
                int curr3 = _solve(j+1,n-1,0,m-1,grid);

                ans = min(ans,curr1+curr2+curr3);

            }
        }

        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){

                int curr1 = _solve(0,n-1,0,i,grid);
                int curr2 = _solve(0,n-1,i+1,j,grid);
                int curr3 = _solve(0,n-1,j+1,m-1,grid);

                ans = min(ans,curr1+curr2+curr3);

            }
        }

        return ans;

    }
};
