	class Solution {
		public int minimumSum(int[][] a) {
			int ans = a.length * a[0].length;
			for(int k = 0;k < 4;k++) {
				int n = a.length, m = a[0].length;
				for (int j = 0; j <= m; j++) {
					for (int i = 0; i <= n; i++) {
						ans = Math.min(ans, go(a, 0, 0, n, j) + go(a, 0, j, i, m) + go(a, i, j, n, m));
					}
				}
				a = rot(a);
			}

			for(int k = 0;k < 2;k++) {
				int n = a.length, m = a[0].length;
				for (int i = 0; i <= m; i++) {
					for(int j = i+1;j <= m;j++){
//						tr(i, j, m, go(a, 0, 0, n, i),  go(a, 0, i, n, j),  go(a, 0, j, n, m));
						ans = Math.min(ans, go(a, 0, 0, n, i) + go(a, 0, i, n, j) + go(a, 0, j, n, m));
					}
				}
				a = rot(a);
			}

			return ans;
		}

		public int go(int[][] grid, int u, int l, int d, int r) {
			if(d-u <= 0 || r-l <= 0)return Integer.MAX_VALUE / 10;
//			tr(u, l, d, r);
			int n = d-u, m = r-l;
			int minr = Integer.MAX_VALUE, maxr = -1;
			int minc = Integer.MAX_VALUE, maxc = -1;
			for(int i = u;i < d;i++){
				for(int j = l;j < r;j++){
					if(grid[i][j] == 1){
						minr = Math.min(minr, i);
						maxr = Math.max(maxr, i);
						minc = Math.min(minc, j);
						maxc = Math.max(maxc, j);
					}
				}
			}
			if(maxr < minr)return 1;
//			tr("ya", minr, maxr, minc, maxc);
			return (maxr-minr+1) * (maxc-minc+1);
		}

		public static int[][] rot(int[][] a)
		{
			int n = a.length, m = a[0].length;
			int[][] b = new int[m][n];
			for(int i = 0;i < n;i++){
				for(int j = 0;j < m;j++){
					b[j][n-1-i] = a[i][j];
				}
			}
			return b;
		}
	}
