using ll = long long;
template <class T>
using vec = vector<T>;

#define FOR(i, s, e) for (ll i = (ll)s; i < (ll)e; i++)
#define CFOR(i, s, e) for (ll i = (ll)s; i <= (ll)e; i++)
#define TRAV(a, c) for (const auto &a : c)
#define dbg(x) cerr << "ln" << __LINE__ << ": " << #x << " = " << x << endl

class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        auto area = [&](ll x1, ll y1, ll x2, ll y2) -> ll {
            ll mnx = INT_MAX, mny = INT_MAX, mxx = 0, mxy = 0;
            CFOR(i, x1, x2) {
                CFOR(j, y1, y2) {
                    if (grid[i][j] == 1) {
                        mnx = min(mnx, i);
                        mny = min(mny, j);
                        mxx = max(mxx, i);
                        mxy = max(mxy, j);
                    }
                }
            }
            if (mnx == INT_MAX) return 0;
            return (mxx + 1 - mnx) * (mxy + 1 - mny);
        };
        ll n = grid.size();
        ll m = grid[0].size();
        ll ans = INT_MAX;
        FOR(i1, 1, n) {
            FOR(i2, i1 + 1, n) {
                ans = min(ans, area(0, 0, i1 - 1, m - 1) + area(i1, 0, i2 - 1, m - 1) + area(i2, 0, n - 1, m - 1));
            }
        }
        FOR(i1, 1, m) {
            FOR(i2, i1 + 1, m) {
                ans = min(ans, area(0, 0, n - 1, i1 - 1) + area(0, i1, n - 1, i2 - 1) + area(0, i2, n - 1, m - 1));
            }
        }
        FOR(i, 1, n) {
            FOR(j, 1, m) {
                ans = min(ans, area(0, 0, i - 1, m - 1) + area(i, 0, n - 1, j - 1) + area(i, j, n - 1, m - 1));
                ans = min(ans, area(0, 0, i - 1, j - 1) + area(0, j, i - 1, m - 1) + area(i, 0, n - 1, m - 1));

                ans = min(ans, area(0, 0, n - 1, j - 1) + area(0, j, i - 1, m - 1) + area(i, j, n - 1, m - 1));
                ans = min(ans, area(0, 0, i - 1, j - 1) + area(i, 0, n - 1, j - 1) + area(0, j, n - 1, m - 1));
            }
        }
        return ans;
    }
};
