class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        def minArea(si, sj, ei, ej):
            maxi, maxj, mini, minj = 0, 0, 30, 30
            for i in range(si, ei):
                for j in range(sj, ej):
                    if grid[i][j]:
                        maxi = max(maxi, i)
                        maxj = max(maxj, j)
                        mini = min(mini, i)
                        minj = min(minj, j)
            if mini > maxi:
                return 10**6
            return (maxi - mini + 1)*(maxj - minj + 1)

        def case1():
            # divide by 2 horizontal lines
            nonlocal m, n
            ans = 10**6
            for i in range(m):
                for j in range(i+1, m):
                    area = minArea(0, 0, i, n) + minArea(i, 0, j, n) + minArea(j, 0, m, n)
                    ans = min(ans, area)
            return ans

        def case2():
            # divide by 2 vertical lines
            nonlocal m, n
            ans = 10**6
            for i in range(n):
                for j in range(i+1, n):
                    area = minArea(0, 0, m, i) + minArea(0, i, m, j) + minArea(0, j, m, n)
                    ans = min(ans, area)
            return ans

        def case3():
            # divide by horizontal first, then vertical
            nonlocal m, n
            ans = 10**6
            for i in range(m):
                for j in range(n):
                    area = minArea(0, 0, i, n) + minArea(i, 0, m, j) + minArea(i, j, m, n)
                    ans = min(ans, area)
                    # other way
                    area = minArea(0, 0, i, j) + minArea(0, j, i, n) + minArea(i, 0, m, n)
                    ans = min(ans, area)
            return ans

        def case4():
            # divide by vertical first, then horizontal
            nonlocal m, n
            ans = 10**6
            for i in range(n):
                for j in range(m):
                    area = minArea(0, 0, m, i) + minArea(0, i, j, n) + minArea(j, i, m, n)
                    ans = min(ans, area)
                    # other way
                    area = minArea(0, 0, j, i) + minArea(j, 0, m, i) + minArea(0, i, m, n)
                    ans = min(ans, area)
            return ans

        return min(case1(), case2(), case3(), case4())
