class Solution {
public:
    int minimumArea(vector<vector<int>>& grid,int l,int r,int b,int u) {
        int r1=INT_MAX,r2=INT_MIN,l1=INT_MAX,l2=INT_MIN;
        for(int i=b;i<=u;i++){
            for(int j=l;j<=r;j++){
                if(grid[i][j]){
                    r1=min(i,r1);
                    r2=max(i,r2);
                    l1=min(l1,j);
                    l2=max(l2,j);
                }
            }
        }
        if(r1==INT_MAX)return 0;
        return (r2-r1+1)*(l2-l1+1);
    };
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=INT_MAX;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                ans=min(ans,minimumArea(grid,0,m-1,0,i)+minimumArea(grid,0,m-1,i+1,j)+minimumArea(grid,0,m-1,j+1,n-1));
            }
        }
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                ans=min(ans,minimumArea(grid,0,i,0,n-1)+minimumArea(grid,i+1,j,0,n-1)+minimumArea(grid,j+1,m-1,0,n-1));
            }
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ans=min(ans,minimumArea(grid,0,m-1,0,i)+minimumArea(grid,0,j,i+1,n-1)+minimumArea(grid,j+1,m-1,i+1,n-1));
            }
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ans=min(ans,minimumArea(grid,0,m-1,i+1,n-1)+minimumArea(grid,0,j,0,i)+minimumArea(grid,j+1,m-1,0,i));
            }
        }
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                ans=min(ans,minimumArea(grid,0,i,0,n-1)+minimumArea(grid,i+1,m-1,0,j)+minimumArea(grid,i+1,m-1,j+1,n-1));
            }
        }
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                ans=min(ans,minimumArea(grid,i+1,m-1,0,n-1)+minimumArea(grid,0,i,0,j)+minimumArea(grid,0,i,j+1,n-1));
            }
        }
        return ans;
    }
};
