using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

class Solution {
    vector<vector<int>> v;

  public:
    int minimumSum(vector<vector<int>> &_v) {
        v = _v;
        int n = v.size();
        int m = v[0].size();
        int ret = INT_MAX;

        for (int i = 0; i < n - 1; i++) {
            for (int j = i + 1; j < n - 1; j++)
                ret = min(ret, minimumArea(0, i, 0, m - 1) + minimumArea(i + 1, j, 0, m - 1) + minimumArea(j + 1, n - 1, 0, m - 1));

            for (int j = 0; j < m - 1; j++) {
                ret = min(ret, minimumArea(0, i, 0, m - 1) + minimumArea(i + 1, n - 1, 0, j) + minimumArea(i + 1, n - 1, j + 1, m - 1));
                ret = min(ret, minimumArea(0, i, 0, j) + minimumArea(0, i, j + 1, m - 1) + minimumArea(i + 1, n - 1, 0, m - 1));
            }
        }

        for (int i = 0; i < m - 1; i++) {
            for (int j = i + 1; j < m - 1; j++)
                ret = min(ret, minimumArea(0, n - 1, 0, i) + minimumArea(0, n - 1, i + 1, j) + minimumArea(0, n - 1, j + 1, m - 1));

            for (int j = 0; j < n - 1; j++) {
                ret = min(ret, minimumArea(0, n - 1, 0, i) + minimumArea(0, j, i + 1, m - 1) + minimumArea(j + 1, n - 1, i + 1, m - 1));
                ret = min(ret, minimumArea(0, j, 0, i) + minimumArea(j + 1, n - 1, 0, i) + minimumArea(0, n - 1, i + 1, m - 1));
            }
        }

        return ret;
    }

    int minimumArea(int top, int bottom, int left, int right) {
        int t = 100;
        int b = -1;
        int l = 100;
        int r = -1;

        for (int i = top; i <= bottom; i++) {
            for (int j = left; j <= right; j++) {
                if (v[i][j] == 0)
                    continue;

                t = min(t, i);
                b = max(b, i);
                l = min(l, j);
                r = max(r, j);
            }
        }

        return (b - t + 1) * (r - l + 1);
    }
};
