class Solution:

    def minimumSum(self, grid: List[List[int]]) -> int:
        nn = len(grid)
        mm = len(grid[0])

        @cache
        def minimumArea(top_x, top_y, bottom_x, bottom_y):
            x_min = mm - 1
            x_max = 0
            y_min = nn - 1
            y_max = 0
            cur_sum = 0
            for ii in range(top_y, bottom_y + 1):
                for jj in range(top_x, bottom_x + 1):
                    if grid[ii][jj] == 0:
                        continue
                    cur_sum += 1
                    x_min = min(x_min, jj)
                    x_max = max(x_max, jj)
                    y_min = min(y_min, ii)
                    y_max = max(y_max, ii)
            rr = (x_max - x_min + 1) * (y_max - y_min + 1)
            if rr < 0:
                return 0
            return rr

        total_sum = 0
        for ii in range(nn):
            for jj in range(mm):
                total_sum += grid[ii][jj]
        @cache
        def get_two_sum(top_x, top_y, bottom_x, bottom_y):
            cur_max = math.inf
            for ii in range(top_y, bottom_y + 1):
                first = minimumArea(top_x, top_y, bottom_x, ii)
                second = minimumArea(top_x, ii + 1, bottom_x, bottom_y)
                cur_max = min(cur_max, first + second)
                # print(ii, first, second)
            for jj in range(top_x, bottom_x + 1):
                first = minimumArea(top_x, top_y, jj, bottom_y)
                second = minimumArea(jj + 1, top_y, bottom_x, bottom_y)
                cur_max = min(cur_max, first + second)
                # print(jj, first, second)
            return cur_max

        def get_three_sum(top_x, top_y, bottom_x, bottom_y):
            cur_max = math.inf
            for ii in range(top_y, bottom_y + 1):
                first = minimumArea(top_x, top_y, bottom_x, ii)
                second = get_two_sum(top_x, ii + 1, bottom_x, bottom_y)
                # print(ii, first, second)
                cur_max = min(cur_max, first + second)
            for jj in range(top_x, bottom_x + 1):
                first = minimumArea(top_x, top_y, jj, bottom_y)
                second = get_two_sum(jj + 1, top_y, bottom_x, bottom_y)
                # print(jj, first, second)
                cur_max = min(cur_max, first + second)
            for ii in range(top_y, bottom_y + 1):
                for jj in range(top_x, bottom_x + 1):
                    first = minimumArea(top_x, top_y, jj, ii)

                    second1 = minimumArea(jj + 1, top_y, bottom_x, bottom_y)
                    third1 = minimumArea(top_x, ii + 1, jj, bottom_y)

                    second2 = minimumArea(jj + 1, top_y, bottom_x, ii)
                    third2 = minimumArea(top_x, ii + 1, bottom_x, bottom_y)

                    cur_max2 = min(second1 + third1, second2 + third2)
                    cur_max = min(cur_max, cur_max2 + first)
            return cur_max
        # get_two_sum(1, 0, mm - 1, nn -1)
        return get_three_sum(0, 0, mm - 1, nn -1)
