class Solution {
public:

    int local(vector<vector<int>>& grid, int a1, int a2, int b1, int b2) {
        //int n = grid.size();
        //int m = grid[0].size();
        int n2 = a1-1, n1 = a2+1, m2 = b1-1, m1 = b2+1;
        for (int i = a1; i <= a2; ++i){
            for (int j = b1; j <= b2; ++j){
                if (grid[i][j] == 1){
                    n1 = min(n1,i);
                    n2 = max(n2,i);
                    m1 = min(m1,j);
                    m2 = max(m2,j);
                }
            }
        }
        //cout<<a1<<a2<<b1<<b2<<endl;
        if (n2 == a1-1) return -1;
        return (n2-n1+1) * (m2-m1+1);
    }

    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int res = INT_MAX;
        // type1
        for (int index1 = 0; index1 < n-2; ++index1){
            //cout<<"enter1";
            int part1 = local(grid,0,index1,0,m-1);
            if (part1 == -1){
                continue;
            }
            for (int index2 = index1+1; index2 < n-1; ++index2){
                int part2 = local(grid,index1+1, index2,0,m-1);
                if (part2 == -1){
                    continue;
                }
                int part3 = local(grid,index2+1,n-1,0,m-1);
                if (part3 != -1){
                    res = min(res, part1+part2+part3);
                }
            }
        }

        // type2
        for (int index1 = 0; index1 < m-2; ++index1){
            //cout<<"enter2";
