                int part2 = local(grid,0,n-1,index1+1, index2);
                if (part2 == -1){
                    continue;
                }
                int part3 = local(grid,0,n-1,index2+1,m-1);
                if (part3 != -1){
                    res = min(res, part1+part2+part3);
                }
            }
        }

        // type3
        for (int index1 = 0; index1 < n-1; ++index1){
            //cout<<"enter3"<<endl;
