                continue;
            }
            for (int index2 = 0; index2 < m-1; ++index2){
                int part2 = local(grid,index1+1, n-1,0,index2);
                if (part2 == -1){
                    continue;
                }
                int part3 = local(grid,index1+1,n-1,index2+1,m-1);
                if (part3 != -1){
                    res = min(res, part1+part2+part3);
                }
            }
        }


        // type5
        for (int index1 = 0; index1 < n-1; ++index1){
            //cout<<"enter5"<<endl;
