                if (part2 == -1){
                    continue;
                }
                int part3 = local(grid,0,index1,index2+1,m-1);
                if (part3 != -1){
                    res = min(res, part1+part2+part3);
                }
            }
        }

        // type4
        for (int index1 = 0; index1 < m-1; ++index1){
            //cout<<"enter4"<<endl;
            int part1 = local(grid,0,n-1,0,index1);
            //cout<<part1<<" ";
            if (part1 == -1){
                continue;
            }
            for (int index2 = 0; index2 < n-1; ++index2){
                int part2 = local(grid,0,index2,index1+1, m-1);
                if (part2 == -1){
                    continue;
                }
                int part3 = local(grid,index2+1,n-1,index1+1,m-1);
                if (part3 != -1){
                    res = min(res, part1+part2+part3);
                }
            }
        }

        // type6
        for (int index1 = 0; index1 < m-1; ++index1){
            //cout<<"enter6"<<endl;
            int part1 = local(grid,0,n-1,index1+1,m-1);
            cout<<part1<<" ";
