            }
            for (int index2 = 0; index2 < n-1; ++index2){
                int part2 = local(grid,0,index2,0, index1);
                if (part2 == -1){
                    continue;
                }
                int part3 = local(grid,index2+1,n-1,0,index1);
                if (part3 != -1){
                    res = min(res, part1+part2+part3);
                }
            }
        }

        return res;

    }
