int minarea(int uu, int dd, int ll, int rr,vector<vector<int>>& grid ){
    int u=dd+1, d=0, l=rr+1, r=0, f=0;
    for (int i=uu; i<=dd; i++){
        for (int j=ll; j<=rr; j++){
            if (grid[i][j]==1){
                u=min(u, i);
                d=max(d, i);
                l=min(l, j);
                r=max(r, j);
                f=1;
            }
        }
    }
    if (f==0) return 0;
    return (d-u+1)*(r-l+1);
}

class Solution {
public:

    int minimumSum(vector<vector<int>>& grid) {
        int ans=INT_MAX;
        int n=grid.size();
        int m=grid[0].size();
        for (int i=0; i<n-1; i++){
            for (int j=0; j<m-1; j++){
                ans=min(ans, minarea(0, i, 0, j, grid)+minarea(i+1, n-1,0, j, grid)+minarea(0, n-1, j+1, m-1, grid));
                ans=min(ans, minarea(0, i, 0, j, grid)+minarea(i+1, n-1,0, m-1, grid)+minarea(0, i, j+1, m-1, grid));
                ans=min(ans, minarea(0, n-1, 0, j, grid)+minarea(i+1, n-1,j+1, m-1, grid)+minarea(0, i, j+1, m-1, grid));
                ans=min(ans, minarea(0, i, 0, m-1, grid)+minarea(i+1, n-1,0, j, grid)+minarea(i+1, n-1,j+1, m-1, grid));

            }
        }

        for (int l=0; l<m; l++){
            for (int r=l+2; r<m; r++){
                ans=min(ans, minarea(0, n-1, 0, l, grid)+minarea(0, n-1,l+1, r-1, grid)+minarea(0, n-1,r, m-1, grid));
            }
        }

        for (int u=0; u<n; u++){
            for (int d=u+2; d<n; d++){
                ans=min(ans, minarea(0, u, 0, m-1, grid)+minarea(u+1, d-1,0, m-1, grid)+minarea(d, n-1,0, m-1, grid));
            }
        }

        return ans;

    }
};
