class Solution {
public:

    vector<vector<int>> v1;

    int sol(int x1, int y1, int x2, int y2){
        int xmin=-1,xmax=-1,ymin=-1,ymax=-1;
        for(int i=x1;i<=x2;i++){
            bool ok=false;
            for(int j=y1;j<=y2;j++){
                if(v1[i][j]==1) ok=true;
            }
            if(ok){
                if(xmin==-1){
                    xmin=i;
                    xmax=i;
                }
                else xmax=i;
            }
        }
        for(int j=y1;j<=y2;j++){
            bool ok=false;
            for(int i=x1;i<=x2;i++){
                if(v1[i][j]==1) ok=true;
            }
            if(ok){
                if(ymin==-1){
                    ymin=j;
                    ymax=j;
                }
                else ymax=j;
            }
        }
        int ans=(xmax-xmin+1)*(ymax-ymin+1);
        if(ans==0) ans=1;
        return ans;
    }

    int minimumSum(vector<vector<int>>& v) {
        int n=(int)v.size();
        int m=(int)v[0].size();
        int cnt=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++) if(v[i][j]==1) cnt++;
        }
        if(cnt<=3) return 3;
        int ans=1e9;
        v1=v;
        //case1
        for(int j=0;j<m-1;j++){
            int var1=sol(0,0,n-1,j);
            for(int i=0;i<n-1;i++){
                int var2=sol(0,j+1,i,m-1);
                int var3=sol(i+1,j+1,n-1,m-1);
                ans=min(ans,var1+var2+var3);
            }
        }
        //case2
        for(int j=m-1;j>0;j--){
            int var1=sol(0,j,n-1,m-1);
            for(int i=0;i<n-1;i++){
                int var2=sol(0,0,i,j-1);
                int var3=sol(i+1,0,n-1,j-1);
                ans=min(ans,var1+var2+var3);
            }
        }
        //case3
        for(int i=0;i<n-1;i++){
            int var1=sol(0,0,i,m-1);
            for(int j=0;j<m-1;j++){
                int var2=sol(i+1,0,n-1,j);
                int var3=sol(i+1,j+1,n-1,m-1);
                ans=min(ans,var1+var2+var3);
            }
        }
        //case4
        for(int i=n-1;i>0;i--){
            int var1=sol(i,0,n-1,m-1);
            for(int j=0;j<m-1;j++){
                int var2=sol(0,0,i-1,j);
                int var3=sol(0,j+1,i-1,m-1);
                ans=min(ans,var1+var2+var3);
            }
        }
        //case5
        for(int i=0;i<n-2;i++){
            int var1=sol(0,0,i,m-1);
            for(int i1=i+1;i1<n-1;i1++){
                int var2=sol(i+1,0,i1,m-1);
                int var3=sol(i1+1,0,n-1,m-1);
                ans=min(ans,var1+var2+var3);
            }
        }
        //case6
        for(int j=0;j<m-2;j++){
            int var1=sol(0,0,n-1,j);
            for(int j1=j+1;j1<m-1;j1++){
                int var2=sol(0,j+1,n-1,j1);
                int var3=sol(0,j1+1,n-1,m-1);
                ans=min(ans,var1+var2+var3);
            }
        }
        return ans;
    }
};
