class Solution {
public:
    int minimumSum(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        int l=m-1;
        int r=0;
        int u=n-1;
        int d=0;
        int ans= 1e9;
        int dp1[n][m];
        memset(dp1, 0, sizeof(dp1));
        int dp2[n][m];
        memset(dp2, 0, sizeof(dp2));
        int dp3[n][m];
        memset(dp3, 0, sizeof(dp3));
        int dp4[n][m];
        memset(dp4, 0, sizeof(dp4));
        for(int i1=0; i1<n; i1++)
        {
            for(int j1=0; j1<m; j1++)
            {
                l=m-1; r=0; u=n-1; d=0;
                bool f=0;
                for(int i=0; i<=i1; i++){
                    for(int j=0; j<=j1; j++){
                        if(a[i][j]==1){
                            l = min(l, j);
                            r = max(r, j);
                            u = min(u, i);
                            d = max(d, i);
                            f=1;
                        }
                    }
                }
                if(f==1){
                    dp1[i1][j1] = (r-l+1)*(d-u+1);
                }
                f=0;
                l=m-1; r=0; u=n-1; d=0;
                for(int i=0; i<=i1; i++){
                    for(int j=m-1; j>=j1; j--){
                        if(a[i][j]==1){
                            l = min(l, j);
                            r = max(r, j);
                            u = min(u, i);
                            d = max(d, i);
                            f=1;
                        }
                    }
                }
                if(f==1){
                    dp2[i1][j1] = (r-l+1)*(d-u+1);
                }
                f=0;
                l=m-1; r=0; u=n-1; d=0;
                for(int i=n-1; i>=i1; i--){
                    for(int j=0; j<=j1;j++){
                        if(a[i][j]==1){
                            l = min(l, j);
                            r = max(r, j);
                            u = min(u, i);
                            d = max(d, i);
                            f=1;
                        }
                    }
                }
                if(f==1){
                    dp3[i1][j1] = (r-l+1)*(d-u+1);
                }
                f=0;
                l=m-1; r=0; u=n-1; d=0;
                for(int i=n-1; i>=i1; i--){
                    for(int j=m-1; j>=j1; j--){
                        if(a[i][j]==1){
                            l = min(l, j);
                            r = max(r, j);
                            u = min(u, i);
                            d = max(d, i);
                            f=1;
                        }
                    }
                }
                if(f==1){
                    dp4[i1][j1] = (r-l+1)*(d-u+1);
                }
            }
        }


        // case 1
        for(int j=0; j<m; j++)
        {
            l=m-1; r=0; u=n-1; d=0;
            bool f=0;
            // [0, j], [j+1, k], [k+1, m-1]
            for(int k=j+1; k+1<m; k++)
            {
                for(int i=0; i<n; i++){
                    if(a[i][k]==1){
                        f=1;
                        l = min(l, k);
                        r = max(r, k);
                        u = min(u, i);
                        d = max(d, i);
                    }
                }
                if(f==1){
                    int curr = (r-l+1)*(d-u+1);
                    if(dp1[n-1][j]!=0 && dp2[n-1][k+1]!=0){
                        ans = min(ans, dp1[n-1][j] + curr + dp2[n-1][k+1]);
                    }
                }
            }
        }

        // case 2
        for(int i=0; i<n; i++)
        {
            int up;
            l=m-1; r=0; up=n-1; d=0;
            bool f=0;
            // [0, i], [i+1, u], [u+1, n-1]
            for(int u=i+1; u+1<n; u++)
            {
                for(int j=0; j<m; j++){
                    if(a[u][j]==1){
                        f=1;
                        l = min(l, j);
                        r = max(r, j);
                        up = min(up, u);
                        d = max(d, u);
                    }
                }
                if(f==1){
                    int curr = (r-l+1)*(d-up+1);
                    if(dp1[i][m-1]!=0 && dp3[u+1][m-1]!=0){
                        ans = min(ans, dp1[i][m-1] + curr + dp3[u+1][m-1]);
                    }
                }
            }
        }

        // case 3
        for(int i=0; i+1<n; i++)
        {
            for(int j=0; j+1<m; j++)
            {
                int x1 = dp1[i][m-1];
                int x2 = dp3[i+1][j];
                int x3 = dp4[i+1][j+1];
                if(x1!=0 && x2!=0 && x3!=0){
                    ans = min(ans, x1+x2+x3);
                }
            }
        }

        // case 4
        for(int j=0; j+1<m; j++)
        {
            for(int i=0; i+1<n; i++)
            {
                int x1 = dp1[n-1][j];
                int x2 = dp2[i][j+1];
                int x3 = dp4[i+1][j+1];
                if(x1!=0 && x2!=0 && x3!=0){
                    ans = min(ans, x1+x2+x3);
                }
            }
        }

        // case 5
        for(int j=m-1; j>=1; j--)
        {
            for(int i=0; i+1<n; i++)
            {
                int x1 = dp2[n-1][j];
                int x2 = dp1[i][j-1];
                int x3 = dp3[i+1][j-1];
                if(x1!=0 && x2!=0 && x3!=0){
                    ans = min(ans, x1+x2+x3);
                }
            }
        }

        // case 6
        for(int i=n-1; i>=1; i--)
        {
            for(int j=0; j+1<m; j++)
            {
                int x1 = dp3[i][m-1];
                int x2 = dp1[i-1][j];
                int x3 = dp2[i-1][j+1];
                if(x1!=0 && x2!=0 && x3!=0){
                    ans = min(ans, x1+x2+x3);
                }
            }
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0; j<m; j++){
        //         cout << dp1[i][j] << " ";
        //     }
        //     cout << endl;
        // }
        return ans;

    }
};
