class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        r,c=len(grid),len(grid[0])


        lf=0
        rt=1
        tp=2
        bt=3
        ret=1<<50
        def area(rect):
            return (rect[rt]-rect[lf]+1)*(rect[bt]-rect[tp]+1)

        def get_rec():
            return [c,-1,r,-1]

        rec=get_rec()
        def cal(rec):
            # print(rec)
            rect=get_rec()
            for i in range(rec[tp],rec[bt]+1):
                for j in range(rec[lf],rec[rt]+1):
                    if grid[i][j]:
                        rect[lf]=min(rect[lf],j)
                        rect[rt]=max(rect[rt],j)
                        rect[tp]=min(rect[tp],i)
                        rect[bt]=i
            # print(rect)
            return area(rect)

        for i in range(r-1):
            rec=[0,c-1,0,i]
            a1=cal(rec)

            if a1:
                for j in range(c-1):
                    rec2=[0,j,i+1,r-1]
                    a2=cal(rec2)
                    rec3=[j+1,c-1,i+1,r-1]
                    a3=cal(rec3)
                    if a2 and a3:
                        ret=min(ret,a1+a2+a3)

                for j in range(i+1,r-1):
                    rec2=[0,c-1,i+1,j]
                    a2=cal(rec2)
                    rec3=[0,c-1,j+1,r-1]
                    a3=cal(rec3)
                    if a2 and a3:
                        ret=min(ret,a1+a2+a3)
        rec=get_rec()   
        for i in range(r-1,0,-1):
            rec=[0,c-1,i,r-1]
            a1=cal(rec)
            if a1:
                for j in range(c-1):
                    rec2=[0,j,0,i-1]
                    a2=cal(rec2)
                    rec3=[j+1,c-1,0,i-1]
                    a3=cal(rec3)
                    if a2 and a3:
                        ret=min(ret,a1+a2+a3)

        for i in range(c-1):
            rec=[0,i,0,r-1]
            a1=cal(rec)
            if a1:
                for j in range(i+1,c-1):
                    rec2=[i+1,j,0,r-1]
                    a2=cal(rec2)
                    rec3=[j+1,c-1,0,r-1]
                    a3=cal(rec3)
                    if a2 and a3:
                        # if sum([a1,a2,a3])==4:
                        #     print((i,j),a1,a2,a3)
                        ret=min(ret,a1+a2+a3)

        for i in range(c-1):
            rec=[0,i,0,r-1]
            a1=cal(rec)
            if a1:
                for j in range(r-1):
                    rec2=[i+1,c-1,0,j]
                    a2=cal(rec2)
                    rec3=[i+1,c-1,j+1,r-1]
                    a3=cal(rec3)
                    if a2 and a3:
                        # if sum([a1,a2,a3])==4:
                        #     print((i,j),a1,a2,a3)
                        ret=min(ret,a1+a2+a3)
        for i in range(c-1,0,-1):
            rec=[i,c-1,0,r-1]
            a1=cal(rec)
            if a1:
                for j in range(r-1):
                    rec2=[0,i-1,0,j]
                    a2=cal(rec2)
                    rec3=[0,i-1,j+1,r-1]
                    a3=cal(rec3)
                    if a2 and a3:
                        # if sum([a1,a2,a3])==4:
                        #     print((i,j),a1,a2,a3)
                        ret=min(ret,a1+a2+a3)      
        return ret
