class Solution {
public:
    int calcarea(vector <vector <int> > &arr, int r1, int r2, int c1, int c2){
        int minx = 1e9, maxx = 0, miny = 1e9, maxy = 0;
        for(int i = r1; i <= r2; i++){
            for(int j = c1; j <= c2; j++){
                if(arr[i][j] == 1){
                    minx = min(minx, i);
                    miny = min(miny, j);
                    maxx = max(maxx, i);
                    maxy = max(maxy, j);
                }
            }
        }
        if(minx == 1e9) return 0;
        return (maxx - minx + 1) * (maxy - miny + 1);
    }

    int solve(vector <vector <int> > &arr, vector <vector <int> > &opp, int cnt,int r1, int r2, int c1, int c2){
        if(cnt == 2){
            // base case

            int thisans =  calcarea(arr,r1, r2, c1, c2);
            // cout<<r1<<" "<<r2<<" "<<c1<<" "<<c2<<" --> "<<endl;
            // cout<<thisans<<endl;
            return thisans;
        }
        if(cnt == 1){
            int ans = 1e9;
            for(int j = c1; j < c2; j++){
                int thisans = solve(arr, opp, 2, r1, r2, c1, j) + solve(arr, opp, 2 , r1, r2, j+1, c2);
                // cout<<"1 row --> for i = "<<j<<" --> "<<thisans<<endl;
                ans = min(thisans , ans);
            }

            for(int j = r1; j < r2; j++){
                int thisans = solve(opp, arr, 2, c1, c2, r1, j) + solve(opp, arr, 2, c1, c2, j+1, r2);
                // int x = solve(opp, arr, 2, c1, c2, r1, j);
                // int y = solve(opp, arr, 2, c1, c2, j+1, r2);
                // cout<<x<<endl;
                // cout<<y<<endl;
                // cout<<"1 col --> for i = "<<j<<" --> "<<thisans<<endl;
                ans = min(ans , thisans);
            }
            return ans;
        }

        if(cnt == 0){
            int ans = 1e9;
            int n = arr.size();
            for(int i = 0 ; i < arr[0].size() - 1; i++){
                int thisans = solve(arr, opp, 2, 0, n-1, 0, i ) + solve(arr, opp, 1, 0, n-1, i+1, arr[0].size() -1 );
                thisans = min(thisans , solve(arr, opp, 1, 0, n-1, 0, i ) + solve(arr, opp, 2, 0, n-1, i+1, arr[0].size() -1 ));
                // int x = solve(arr, opp, 1, 0, n-1, 0, i ), y = solve(arr, opp, 1, 0, n-1, i+1, arr[0].size() -1 );
                // cout<<x<<" "<<y<<endl;
                // cout<<"0 row --> for i = "<<i<<" --> "<<thisans<<endl;
                ans = min(ans, thisans);
            }

            n = opp.size();
            for(int i = 0 ; i < arr.size() - 1; i++){
                int thisans = solve( opp, arr,  2, 0, n-1, 0, i ) + solve(opp, arr, 1, 0, n-1, i+1, arr.size() - 1);
                thisans = min(thisans,solve( opp, arr,  1, 0, n-1, 0, i ) + solve(opp, arr, 2, 0, n-1, i+1, arr.size() - 1) );
                // cout<<"0 col --> for i = "<<i<<" --> "<<thisans<<endl;
                ans = min(ans, thisans);
            }

            return ans;
        }

        return -1;
    }

    int minimumSum(vector<vector<int>>& arr) {
        int n = arr.size();
        int m = arr[0].size();
        vector <vector <int> > opp(m, vector <int> (n));
        for(int i = 0 ; i < m; i++){
            for(int j =0; j < n; j++){
                opp[i][j] = arr[j][i];
            }
        }

//         for(int i = 0; i < opp.size(); i++){
//             for(int j = 0; j < opp[0].size(); j++)
//                 cout<<opp[i][j]<<' ';

//             cout<<endl;
//         }

        int ans = solve(arr, opp, 0, 0, n-1, 0, m-1);
        return ans;
    }
};
