class Solution {
    public int minimumSum(int[][] grid) {
		int res = Integer.MAX_VALUE;
		int R = grid.length;
		int C = grid[0].length;
		// top to down
		for (int i = 0; i < R - 1; i++) {
			int a1 = minimumArea(grid, 0, 0, i, C - 1);
			for (int j = 0; j < C - 1; j++) {
				int a2 = minimumArea(grid, i + 1, 0, R - 1, j);
				int a3 = minimumArea(grid, i + 1, j + 1, R - 1, C - 1);
				res = Math.min(res, a1 + a2 + a3);
			}
		}

		for (int i = R - 1; i > 0; i--) {
			int a1 = minimumArea(grid, i, 0, R - 1, C - 1);
			for (int j = 0; j < C - 1; j++) {
				int a2 = minimumArea(grid, 0, 0, i - 1, j);
				int a3 = minimumArea(grid, 0, j + 1, i - 1, C - 1);
				res = Math.min(res, a1 + a2 + a3);
			}
		}
		// left to right
		for (int i = 0; i < C - 1; i++) {
			int a1 = minimumArea(grid, 0, 0, R - 1, i);
			for (int j = 0; j < R - 1; j++) {
				int a2 = minimumArea(grid, 0, i + 1, j, C - 1);
				int a3 = minimumArea(grid, j + 1, i + 1, R - 1, C - 1);
				res = Math.min(res, a1 + a2 + a3);
			}
		}

		for (int i = C - 1; i > 0; i--) {
			int a1 = minimumArea(grid, 0, i, R - 1, C - 1);
			for (int j = 0; j < R - 1; j++) {
				int a2 = minimumArea(grid, 0, 0, j, i - 1);
				int a3 = minimumArea(grid, j + 1, 0, R - 1, i - 1);
				res = Math.min(res, a1 + a2 + a3);
			}
		}
		//
		for (int i = 0; i < R - 2; i++) {
			for (int j = i + 1; j < R - 1; j++) {

				int a1 = minimumArea(grid, 0, 0, i, C - 1);
				int a2 = minimumArea(grid, i + 1, 0, j, C - 1);
				int a3 = minimumArea(grid, j + 1, 0, R - 1, C - 1);
				res = Math.min(res, a1 + a2 + a3);

			}
		}

		for (int i = 0; i < C - 2; i++) {
			for (int j = i + 1; j < C - 1; j++) {

				int a1 = minimumArea(grid, 0, 0, R - 1, i);
				int a2 = minimumArea(grid, 0, i + 1, R - 1, j);
				int a3 = minimumArea(grid, 0, j + 1, R - 1, C - 1);
				res = Math.min(res, a1 + a2 + a3);

			}
		}
		return res;
	}

	public int minimumArea(int[][] grid, int r1, int c1, int r2, int c2) {
		int t = Integer.MAX_VALUE, d = 0, l = Integer.MAX_VALUE, r = 0;
		for (int i = r1; i <= r2; i++) {
			for (int j = c1; j <= c2; j++) {
				var g = grid[i][j];
				if (g == 1) {
					l = Math.min(j, l);
					r = Math.max(j, r);
					t = Math.min(i, t);
					d = Math.max(i, d);
				}
			}
		}

		return (d - t + 1) * (r - l + 1);
	}
}
