class Solution {
    vector<int> find(int stx,int enx,int sty,int eny,vector<vector<int>>&grid)
    {
        int xmn=1001,xmx=-1,ymn=1001,ymx=-1;
        for(int i=stx;i<enx;i++)
        {
            for(int j=sty;j<eny;j++)
            {
                if(grid[i][j]==1)
                {
                    xmn=min(xmn,i);
                    xmx=max(xmx,i);
                    ymn=min(ymn,j);
                    ymx=max(ymx,j);
                }
            }
        }
        return {xmn,xmx,ymn,ymx};
    }
    int findarea(vector<int>a)
    {
        int ans=(a[1]-a[0]+1)*(a[3]-a[2]+1);
        return ans;
    }
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=1e9;
        int cnt=0;
        for(int i=0;i<n;i++) for(int j=0;j<m;j++) if(grid[i][j]==1) cnt+=1;
        for(int szx=1;szx<=n-1;szx++)
        {
            for(int szy=1;szy<=m-1;szy++)
            {
                vector<vector<int>>pts;
                pts.push_back(find(szx,n,0,szy,grid));
                pts.push_back(find(0,szx,0,szy,grid));
                pts.push_back(find(0,szx,szy,m,grid));
                pts.push_back(find(szx,n,szy,m,grid));

                //mix 0,1
                int temp=findarea(pts[2])+findarea(pts[3]);
                vector<int>pk=pts[0];
                pk[0]=min(pk[0],pts[1][0]);
                pk[1]=max(pk[1],pts[1][1]);
                pk[2]=min(pk[2],pts[1][2]);
                pk[3]=max(pk[3],pts[1][3]);
                temp+=findarea(pk);
                ans=min(ans,temp);
                //mix 1,2
                temp=findarea(pts[0])+findarea(pts[3]);
                pk=pts[1];
                pk[0]=min(pk[0],pts[2][0]);
                pk[1]=max(pk[1],pts[2][1]);
                pk[2]=min(pk[2],pts[2][2]);
                pk[3]=max(pk[3],pts[2][3]);
                temp+=findarea(pk);
                ans=min(ans,temp);
                //mix 2,3
                temp=findarea(pts[0])+findarea(pts[1]);
                pk=pts[2];
                pk[0]=min(pk[0],pts[3][0]);
                pk[1]=max(pk[1],pts[3][1]);
                pk[2]=min(pk[2],pts[3][2]);
                pk[3]=max(pk[3],pts[3][3]);
                temp+=findarea(pk);
                ans=min(ans,temp);
                //mix 0,3
                temp=findarea(pts[1])+findarea(pts[2]);
                pk=pts[0];
                pk[0]=min(pk[0],pts[3][0]);
                pk[1]=max(pk[1],pts[3][1]);
                pk[2]=min(pk[2],pts[3][2]);
                pk[3]=max(pk[3],pts[3][3]);
                temp+=findarea(pk);
                ans=min(ans,temp);
            }
        }
        for(int szx=1;szx<=n-1;szx++)
        {
            for(int szm=szx+1;szm<=n-1;szm++)
            {
                vector<vector<int>>pts;
                pts.push_back(find(szx,szm,0,m,grid));
                pts.push_back(find(0,szx,0,m,grid));
                pts.push_back(find(szm,n,0,m,grid));
                int temp=findarea(pts[1])+findarea(pts[2])+findarea(pts[0]);
                ans=min(ans,temp);
            }
        }
        for(int szx=1;szx<=m-1;szx++)
        {
            for(int szm=szx+1;szm<=m-1;szm++)
            {
                vector<vector<int>>pts;
                pts.push_back(find(0,n,szx,szm,grid));
                pts.push_back(find(0,n,0,szx,grid));
                pts.push_back(find(0,n,szm,m,grid));
                int temp=findarea(pts[1])+findarea(pts[2])+findarea(pts[0]);
                ans=min(ans,temp);
            }
        }
        return ans;
    }
};
