class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        def find_rectangle(top, bottom, left, right):
            min_left, max_right = n, 0
            min_top, max_bottom = m, 0
            for i in range(top, bottom):
                for j in range(left, right):
                    if grid[i][j] == 1:
                        min_left = min(min_left, j)
                        max_right = max(max_right, j)
                        min_top = min(min_top, i)
                        max_bottom = max(max_bottom, i)
            return (max_right - min_left + 1) * (max_bottom - min_top + 1) if min_left <= max_right else 0

        min_area = float('inf')

        for i in range(1, m-1):
            for j in range(i+1, m):
                area1 = find_rectangle(0, i, 0, n)
                area2 = find_rectangle(i, j, 0, n)
                area3 = find_rectangle(j, m, 0, n)
                min_area = min(min_area, area1 + area2 + area3)

        # All vertical divisions
        for i in range(1, n-1):
            for j in range(i+1, n):
                area1 = find_rectangle(0, m, 0, i)
                area2 = find_rectangle(0, m, i, j)
                area3 = find_rectangle(0, m, j, n)
                min_area = min(min_area, area1 + area2 + area3)

        # 2 horizontal, 1 vertical
        for i in range(1, m):
            for j in range(1, n):
                area1 = find_rectangle(0, i, 0, n)
                area2 = find_rectangle(i, m, 0, j)
                area3 = find_rectangle(i, m, j, n)
                min_area = min(min_area, area1 + area2 + area3)

        # 1 horizontal, 2 vertical
        for i in range(1, m):
            for j in range(1, n):
                area1 = find_rectangle(0, i, 0, j)
                area2 = find_rectangle(0, i, j, n)
                area3 = find_rectangle(i, m, 0, n)
                min_area = min(min_area, area1 + area2 + area3)

        # 2 vertical, 1 horizontal
        for i in range(1, n):
            for j in range(1, m):
                area1 = find_rectangle(0, m, 0, i)
                area2 = find_rectangle(0, j, i, n)
                area3 = find_rectangle(j, m, i, n)
                min_area = min(min_area, area1 + area2 + area3)

        # 1 vertical, 2 horizontal
        for i in range(1, n):
            for j in range(1, m):
                area1 = find_rectangle(0, j, 0, i)
                area2 = find_rectangle(j, m, 0, i)
                area3 = find_rectangle(0, m, i, n)
                min_area = min(min_area, area1 + area2 + area3)

        return min_area
