class Solution {
public:

    int Col(vector<vector<int>>& arr, int colStart, int colEnd, int rowStart, int rowEnd) {

        int top = 1e9, bottom = -1e9, left = 1e9, right = -1e9;

        int numRows = arr.size(), numCols = arr[0].size();

        int minArea = 1e9;

        for(int i = rowStart; i <= rowEnd; i++){
            for(int j = colStart; j <= colEnd; j++){
                if(arr[i][j]){
                    top = min(top, i);
                    bottom = max(bottom, i);
                    left = min(left, j);
                    right = max(right, j);
                }
            }
        }

        if(top == 1e9){
            return 0;
        }

        int width = right - left + 1;
        int height = bottom - top + 1;

        minArea = width * height;

        return minArea;
    }

    int row(vector<vector<int>>& arr, int rowStart, int rowEnd, int colStart, int colEnd) {

        int top = 1e9, bottom = -1e9, left = 1e9, right = -1e9;

        int numRows = arr.size(), numCols = arr[0].size();

        int minArea = 1e9;

        for(int i = rowStart; i <= rowEnd; i++){
            for(int j = colStart; j <= colEnd; j++){
                if(arr[i][j]){
                    top = min(top, i);
                    bottom = max(bottom, i);
                    left = min(left, j);
                    right = max(right, j);
                }
            }
        }

        if(top == 1e9){
            return 0;
        }

        int width = right - left + 1;
        int height = bottom - top + 1;

        minArea = width * height;

        return minArea;
    }

    int minimumSum(vector<vector<int>>& arr) {

        int numRows = arr.size(), numCols = arr[0].size();

        int minSum = 1e9;

        // Check columns
        for(int i = 0; i < numCols - 2; i++){
            for(int j = i + 1; j < numCols - 1; j++){
                for(int k = j + 1; k < numCols; k++){
                    int area1 = Col(arr, 0, j - 1, 0, numRows - 1);
                    int area2 = Col(arr, j, k - 1, 0, numRows - 1);
                    int area3 = Col(arr, k, numCols - 1, 0, numRows - 1);

                    if(area1 && area2 && area3){
                        minSum = min(minSum, area1 + area2 + area3);
                    }
                }
            }
        }

        // Check rows
        for(int i = 0; i < numRows - 2; i++){
            for(int j = i + 1; j < numRows - 1; j++){
                for(int k = j + 1; k < numRows; k++){
                    int area1 = row(arr, 0, j - 1, 0, numCols - 1);
                    int area2 = row(arr, j, k - 1, 0, numCols - 1);
                    int area3 = row(arr, k, numRows - 1, 0, numCols - 1);

                    if(area1 && area2 && area3){
                        minSum = min(minSum, area1 + area2 + area3);
                    }
                }
            }
        }

        // Check upper 2 rows
        for(int i = 0; i < numRows - 1; i++){
            int area3 = row(arr, i + 1, numRows - 1, 0, numCols - 1);
            for(int j = 0; j < numCols - 1; j++){
                int area1 = Col(arr, 0, j, 0, i);
                int area2 = Col(arr, j + 1, numCols - 1, 0, i);
                if(area1 && area2 && area3){
                    minSum = min(minSum, area1 + area2 + area3);
                }
            }
        }

        // Check upper 1 row
        for(int i = 0; i < numRows - 1; i++){
            int area3 = row(arr, 0, i, 0, numCols - 1);
            for(int j = 0; j < numCols - 1; j++){
                int area1 = Col(arr, 0, j, i + 1, numRows - 1);
                int area2 = Col(arr, j + 1, numCols - 1, i + 1, numRows - 1);
                if(area1 && area2 && area3){
                    minSum = min(minSum, area1 + area2 + area3);
                }
            }
        }

        // Check right 2 columns
        for(int i = 0; i < numCols - 1; i++){
            int area3 = Col(arr, i + 1, numCols - 1, 0, numRows - 1);
            for(int j = 0; j < numRows - 1; j++){
                int area1 = row(arr, 0, j, 0, i);
                int area2 = row(arr, j + 1, numRows - 1, 0, i);
                if(area1 && area2 && area3){
                    minSum = min(minSum, area1 + area2 + area3);
                }
            }
        }

        // Check right 1 column
        for(int i = 0; i < numCols - 1; i++){
            int area3 = Col(arr, 0, i, 0, numRows - 1);
            for(int j = 0; j < numRows - 1; j++){
                int area1 = row(arr, 0, j, i + 1, numCols - 1);
                int area2 = row(arr, j + 1, numRows - 1, i + 1, numCols - 1);
                if(area1 && area2 && area3){
                    minSum = min(minSum, area1 + area2 + area3);
                }
            }
        }

        return minSum;
    }
};
