class Solution {
public:
    #define ll long int
    long get_the_maximum_area_of_rectangle(int a,int b,int c,int d,vector<vector<int>> &given_vector_int_the_question)
    {
        long abcd=123456,efgh=0,ijkl=123456,xyz=0;
        a--;
        b--;
        c--;
        d--;
        for(long i=a;i<=b;i++)
        {
            for(long j=c;j<=d;j++)
            {
                if(given_vector_int_the_question[i][j]==1)
                {
                    abcd=min(abcd,j+1);
                    efgh=max(efgh,j+1);
                    ijkl=min(ijkl,i+1);
                    xyz=i+1;
                }
            }
        }
        if(abcd==123456) return 10000000;
        return (efgh-abcd+1)*(xyz-ijkl+1);
    }
    int minimumSum(vector<vector<int>>& given_vector_int_the_question) {
        int n=given_vector_int_the_question.size();
        int m=given_vector_int_the_question[0].size();
        long int final_answer_of_the_question=1e9;
        for(int i=1;i<n;i++)
   {
       for(int j=1;j<m;j++)
       {
           long int answeijkl=get_the_maximum_area_of_rectangle(1,i,1,m,given_vector_int_the_question);
           long int answexyz=get_the_maximum_area_of_rectangle(i+1,n,1,j,given_vector_int_the_question);
           long int answer3=get_the_maximum_area_of_rectangle(i+1,n,j+1,m,given_vector_int_the_question);
           final_answer_of_the_question=min(final_answer_of_the_question,answeijkl+answexyz+answer3);
           answeijkl=get_the_maximum_area_of_rectangle(1,i,1,j,given_vector_int_the_question);
           answexyz=get_the_maximum_area_of_rectangle(1,i,j+1,m,given_vector_int_the_question);
           answer3=get_the_maximum_area_of_rectangle(i+1,n,1,m,given_vector_int_the_question);
           final_answer_of_the_question=min(final_answer_of_the_question,answeijkl+answexyz+answer3);
       }
   }

   for(int i=1;i<m;i++)
   {
       for(int j=1;j<n;j++)
       {
           long int answeijkl=get_the_maximum_area_of_rectangle(1,n,1,i,given_vector_int_the_question);
           long int answexyz=get_the_maximum_area_of_rectangle(1,j,i+1,m,given_vector_int_the_question);
           long int answer3=get_the_maximum_area_of_rectangle(j+1,n,i+1,m,given_vector_int_the_question);
           final_answer_of_the_question=min(final_answer_of_the_question,answeijkl+answexyz+answer3);
            answeijkl=get_the_maximum_area_of_rectangle(1,j,1,i,given_vector_int_the_question);
            answexyz=get_the_maximum_area_of_rectangle(j+1,n,1,i,given_vector_int_the_question);
            answer3=get_the_maximum_area_of_rectangle(1,n,i+1,m,given_vector_int_the_question);
           final_answer_of_the_question=min(final_answer_of_the_question,answeijkl+answexyz+answer3);
       }
   }
   for(int i=1;i<m-1;i++)
   {
       for(int k=i+1;k<m;k++)
       {
            long int answeijkl=get_the_maximum_area_of_rectangle(1,n,1,i,given_vector_int_the_question);
              long int answexyz=get_the_maximum_area_of_rectangle(1,n,i+1,k,given_vector_int_the_question);
              long int answer3=get_the_maximum_area_of_rectangle(1,n,k+1,m,given_vector_int_the_question);
              final_answer_of_the_question=min(final_answer_of_the_question,answeijkl+answexyz+answer3);
       }
   }

    for(int i=1;i<n-1;i++)
   {        
           for(int k=i+1;k<n;k++)
           {
              long int answeijkl=get_the_maximum_area_of_rectangle(1,i,1,m,given_vector_int_the_question);
