              final_answer_of_the_question=min(final_answer_of_the_question,answeijkl+answexyz+answer3);
           }
       }

   for(int i=0;i<n;i++)
   {
   sort(given_vector_int_the_question[i].begin(),given_vector_int_the_question[i].end());
   }
    return final_answer_of_the_question;
    }
