#define ll long long
class Solution {
public:
    ll findar(vector<vector<int>>& grid,ll a,ll b,ll c,ll d){
        ll n = grid.size();
        ll m = grid[0].size();
        vector<ll>vert(m);
        vector<ll>hor(n);
        ll left=-1;
        ll right=-1;
        ll top=-1;
        ll bot=-1;
        for(ll i=a;i<=b;i++){
            bool poke=true;
            for(ll j=c;j<=d;j++){
                if(grid[i][j]==1){
                    poke=false;
                }
            }
            if(!poke){
                left=i;
                break;
            }
        }
        for(ll i=b;i>=a;i--){
            bool poke=true;
            for(ll j=c;j<=d;j++){
                if(grid[i][j]==1){
                    poke=false;
                }
            }
            if(!poke){
                right=i;
                break;
            }
        }
        for(ll i=c;i<=d;i++){
            bool poke=true;
            for(ll j=a;j<=b;j++){
                if(grid[j][i]==1){
                    poke=false;
                }
            }
            if(!poke){
                top=i;
                break;
            }
        }
        for(ll i=d;i>=c;i--){
            bool poke=true;
            for(ll j=a;j<=b;j++){
                if(grid[j][i]==1){
                    poke=false;
                }
            }
            if(!poke){
                bot=i;
                break;
            }
        }
        if(bot==-1 || right==-1)return -1;
        return (bot-top+1)*(right-left+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        ll n = grid.size();
        ll m = grid[0].size();
        ll mini=1e9;
        for(ll i=0;i<n-2;i++){
            for(ll j=i+1;j<n-1;j++){
                ll a=findar(grid,0,i,0,m-1);
                ll b=findar(grid,i+1,j,0,m-1);
                ll c=findar(grid,j+1,n-1,0,m-1);
                if(a!=-1 && b!=-1 && c!=-1){
                    mini=min(mini,a+b+c);
                }
            }
        }
        for(ll i=0;i<m-2;i++){
            for(ll j=i+1;j<m-1;j++){
                ll a=findar(grid,0,n-1,0,i);
                ll b=findar(grid,0,n-1,i+1,j);
                ll c=findar(grid,0,n-1,j+1,m-1);
                if(a!=-1 && b!=-1 && c!=-1){
                    mini=min(mini,a+b+c);
                }
            }

        }

        for(ll i=0;i<n-1;i++){
            for(ll j=0;j<m-1;j++){
                ll a=findar(grid,0,i,0,m-1);
                ll b=findar(grid,i+1,n-1,0,j);
                ll c=findar(grid,i+1,n-1,j+1,m-1);
                if(a!=-1 && b!=-1 && c!=-1){
                    mini=min(mini,a+b+c);
                }
            }
        }

        for(ll i=0;i<n-1;i++){
            for(ll j=0;j<m-1;j++){
                ll a=findar(grid,0,i,0,j);
                ll b=findar(grid,0,i,j+1,m-1);
                ll c=findar(grid,i+1,n-1,0,m-1);
                if(a!=-1 && b!=-1 && c!=-1){
                    mini=min(mini,a+b+c);
                }
            }
        }

        for(ll i=0;i<m-1;i++){
            for(ll j=0;j<n-1;j++){
                ll a=findar(grid,0,n-1,0,i);
                ll b=findar(grid,0,j,i+1,m-1);
                ll c=findar(grid,j+1,n-1,i+1,m-1);
                if(a!=-1 && b!=-1 && c!=-1){
                    mini=min(mini,a+b+c);
                }
            }
        }

        for(ll i=0;i<m-1;i++){
            for(ll j=0;j<n-1;j++){
                ll a=findar(grid,0,j,0,i);
                ll b=findar(grid,j+1,n-1,0,i);
                ll c=findar(grid,0,n-1,i+1,m-1);
                if(a!=-1 && b!=-1 && c!=-1){
                    mini=min(mini,a+b+c);
                }
            }
        }

        return mini;

    }
};
