class Solution {
public:
    int check(vector<vector<int>>& grid,int x1,int y1, int x2, int y2){
        int X1 = 1e9, Y1 = 1e9 , X2 = -1, Y2 = -1;
        for(int i = 0; i < grid.size(); ++i){
            for(int j = 0; j < grid[0].size(); ++j){
                if(i >= x1 && i <= x2 && j >= y1 && j <= y2 && grid[i][j]){
                    X1 = min(i, X1);
                    Y1 = min(j, Y1);
                    X2 = max(i, X2);
                    Y2 = max(j, Y2);
                }
            }
        }
        if(X1 == 1e9) return 0;
        int area = (X2 - X1 + 1) * (Y2 - Y1 + 1);
        return area;
    }
    int minimumSum(vector<vector<int>>& grid) {
        int ans = 1e9;
        int n = grid.size();
        int m = grid[0].size();
        for(int i = 0; i < grid.size(); ++i){
            for(int j = 0; j < grid[0].size(); ++j){
                if(i + 1 < grid.size() && j + 1 < grid[0].size()){
                    ans = min(ans, check(grid,0,0,i,m - 1) + check(grid,i + 1,0,n - 1, j) + check(grid, i + 1, j + 1, n - 1, m - 1));
                    ans = min(ans, check(grid, 0,0,n-1,j) + check(grid,0,j+1,i,m-1) + check(grid,i+1,j+1, n - 1, m - 1));
                    ans = min(ans, check(grid,i+1,0,n-1,m -1) + check(grid,0,0,i,j) + check(grid,0,j+1,i,m-1));
                    ans = min(ans, check(grid,0,j+1,n-1,m-1) + check(grid,0,0,i,j) + check(grid,i+1,0,n-1,j));
                }
            }
        }

        for(int i = 0; i < m; ++i){
            for(int j = i + 1; j < m; ++j){
                if(j + 1 >= m) continue;
                ans = min(ans, check(grid,0,0,n-1,i) + check(grid,0,i+1,n-1,j) + check(grid,0,j+1,n-1,m-1));
            }
        }

        for(int i = 0; i < n; ++i){
            for(int j = i + 1; j < n; ++j){
                if(j + 1 >= n) continue;
                ans = min(ans, check(grid,0,0,i,m-1) + check(grid,i+1,0,j,m-1) + check(grid,j+1,0,n-1,m-1));
            }
        }

        return ans;
    }
};
