int ok(vector<vector<int>>& grid,int l1,int r1,int l2,int r2){
    int x1=1e4,y1=1e4,x2=0,y2=0;
    for(int i=l1;i<=l2;i++){
        for(int j=r1;j<=r2;j++){
            if(grid[i][j]==0) continue;
            x1=min(x1,i);
            x2=max(x2,i);
            y1=min(y1,j);
            y2=max(y2,j);
        }
    }
    if(x1==1e9) return -1;
    return (x2-x1+1)*(y2-y1+1);
}

class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=1e9;
        for(int i=0;i<n-1;i++){
            for(int j=0;j<m-1;j++){
                int a=ok(grid,0,0,i,j);
                int b=ok(grid,i+1,0,n-1,m-1);
                int c=ok(grid,0,j+1,i,m-1);
                int b1=ok(grid,i+1,0,n-1,j);
                int c1=ok(grid,0,j+1,n-1,m-1);
                if(a!=-1 && b!=-1 && c!=-1){
                    ans=min(ans,a+b+c);
                }
                // ans=min(ans,a+b1+c1);
                if(a!=-1 && b1!=-1 && c1!=-1){
                    ans=min(ans,a+b1+c1);
                }
            }
        }
        for(int j=0;j<m-1;j++){
            int a=ok(grid,0,0,n-1,j);
            if(a==-1) continue;
            for(int i=0;i<n-1;i++){
                int b1=ok(grid,0,j+1,i,m-1);
                int c1=ok(grid,i+1,j+1,n-1,m-1);
                if(b1!=-1 && c1!=-1){
                    ans=min(ans,a+b1+c1);
                }
            }
            for(int j1=j+1;j1<m-1;j1++){
                int b=ok(grid,0,j+1,n-1,j1);
                int c=ok(grid,0,j1+1,n-1,m-1);
                if(b!=-1 && c!=-1){
                    ans=min(ans,a+b+c);
                }
            }
        }
        for(int i=0;i<n-1;i++){
            int a=ok(grid,0,0,i,m-1);
            if(a==-1) continue;
            for(int j=0;j<m-1;j++){
                int b1=ok(grid,i+1,0,n-1,j);
                int c1=ok(grid,i+1,j+1,n-1,m-1);
                if(b1!=-1 && c1!=-1){
                    ans=min(ans,a+b1+c1);
                }
            }
            for(int i1=i+1;i1<n-1;i1++){
                int b=ok(grid,i+1,0,i1,m-1);
                int c=ok(grid,i1+1,0,n-1,m-1);
                if(b!=-1 && c!=-1){
                    ans=min(ans,a+b+c);
                }
            }
        }
        return ans;
    }
};
