class Solution
{
public:
    int minimumArea(vector<vector<int>> &grid, int ll, int rr, int tt, int dd)
    {
        int l = INT_MAX;
        int r = 0;
        int t = INT_MAX;
        int d = 0;
        for (int i = ll; i <= rr; i++)
        {
            for (int j = tt; j <= dd; j++)
            {
                if (grid[i][j] != 1)
                    continue;
                l = std::min(l, i);
                r = std::max(r, i);
                t = std::min(t, j);
                d = std::max(d, j);
            }
        }
        if (l == INT_MAX)
            return 0;
        return (r - l + 1) * (d - t + 1);
    }

    int minimumSum(vector<vector<int>> &grid)
    {
        int res = INT_MAX;
        int n = grid.size();
        int m = grid[0].size();
        for (int i = 0; i < n; i++)
        {
            for (int j = 0; j < m; j++)
            {
                int a = minimumArea(grid, 0, i, 0, j);
                if (i != n - 1 && j != m - 1)
                {
                    {
                        int b = minimumArea(grid, i + 1, n - 1, 0, j);
                        int c = minimumArea(grid, 0, n - 1, j + 1, m - 1);
                        if (a != 0 && b != 0 && c != 0)
                        {
                            res = std::min(res, a + b + c);
                        }
                    }
                    {
                        int b = minimumArea(grid, 0, i, j + 1, m - 1);
                        int c = minimumArea(grid, i + 1, n - 1, 0, m - 1);
                        if (a != 0 && b != 0 && c != 0)
                        {
                            res = std::min(res, a + b + c);
                        }
                    }
                }
                if (i == n - 1)
                {
                    for(int k=0;k<n;k++) {
                        int b = minimumArea(grid, 0,k, j+1, m-1);
                        int c = minimumArea(grid, k+1, n-1, j+1, m-1);
                        if (a != 0 && b != 0 && c != 0)
                        {
                            res = std::min(res, a + b + c);
                        }
                    }
                    for(int k=j+1;k<m;k++) {
                        int b = minimumArea(grid, 0,n-1, j+1, k);
                        int c = minimumArea(grid, 0,n-1, k+1, m-1);
                        if (a != 0 && b != 0 && c != 0)
                        {
                            res = std::min(res, a + b + c);
                        }
                    }
                }
                if (j == m - 1)
                {
                    for(int k=0;k<m;k++) {
                        int b = minimumArea(grid, i+1, n-1, 0,k);
                        int c = minimumArea(grid, i+1, n-1, k+1,m-1);
                        if (a != 0 && b != 0 && c != 0)
                        {
                            res = std::min(res, a + b + c);
                        }
                    }
                    for(int k=i+1;k<n;k++) {
                        int b = minimumArea(grid, i+1,k, 0, m-1);
                        int c = minimumArea(grid, k+1,n-1,0, m-1);
                        if (a != 0 && b != 0 && c != 0)
                        {
                            res = std::min(res, a + b + c);
                        }
                    }
                }
            }
        }
        return res;
    }
};
