class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        n,m = len(grid),len(grid[0])
        def rect(i,I,j,J):
            r_s,c_s = I,J
            r_b,c_b = i,j
            count = 0
            for x in range(i,I+1):
                for y in range(j,J+1):
                    if grid[x][y]:
                        r_s = min(r_s,x)
                        c_s = min(c_s,y)
                        r_b = max(r_b,x)
                        c_b = max(c_b,y)
                        count += 1
            return r_s,r_b,c_s,c_b, (r_b-r_s+1)*(c_b-c_s+1), count

        def two_rect(i,I,j,J):
            ans = inf
            l_t,r_t,u_t,d_t,useless,c = rect(i,I,j,J)
            for x in range(l_t,r_t):
                useless,useless,useless,useless,s_1,c_1 = rect(l_t,x,u_t,d_t)
                if 0<c_1<c:
                    ans = min(ans, s_1+ rect(x+1,r_t,u_t,d_t)[4])
            for y in range(u_t,d_t):
                useless,useless,useless,useless,s_1,c_1 = rect(l_t,r_t,u_t,y)
                if 0<c_1<c:
                    ans = min(ans, s_1+ rect(l_t,r_t,y+1,d_t)[4])
            return ans

        ans = inf
        l,r,u,d,useless, C = rect(0,n-1,0,m-1)    
        for x in range(l,r):
            useless,useless,useless,useless,s,c = rect(l,x,u,d)
            if 0<c<C-1:
                ans = min(ans, s + two_rect(x+1,r,u,d))

            useless,useless,useless,useless,s,c = rect(x+1,r,u,d)
            if 0<c<C-1:
                ans = min(ans, s + two_rect(l,x,u,d))

        for y in range(u,d):
            useless,useless,useless,useless,s,c = rect(l,r,u,y)
            if 0<c<C-1:
                ans = min(ans, s+two_rect(l,r,y+1,d))

            useless,useless,useless,useless,s,c = rect(l,r,y+1,d)
            if 0<c<C-1:
                ans = min(ans, s+two_rect(l,r,u,y))
        return ans
